import React, { useEffect, useState } from "react";
import axios from "axios";

const Notifications = () => {
    const [notifications, setNotifications] = useState([]);
    const [newNotification, setNewNotification] = useState({ title: "", message: "", recipient: "all" });
    const [error, setError] = useState(""); // State để lưu lỗi nếu có

    useEffect(() => {
        fetchNotifications();
    }, []);

    const fetchNotifications = async () => {
        try {
            const response = await axios.get("http://localhost:5000/notifications/notifications");
            setNotifications(response.data);
        } catch (error) {
            console.error("❌ Lỗi khi lấy thông báo:", error);
            setError("Không thể tải danh sách thông báo. Vui lòng thử lại sau!");
        }
    };

    const handleDelete = async (id) => {
        if (!window.confirm("Bạn có chắc chắn muốn xóa thông báo này?")) return;
        try {
            await axios.delete(`http://localhost:5000/notifications/notifications/${id}`);
            fetchNotifications();
        } catch (error) {
            console.error("❌ Lỗi khi xóa thông báo:", error);
            setError("Không thể xóa thông báo. Vui lòng thử lại sau!");
        }
    };

    const handleCreate = async () => {
        if (!newNotification.title || !newNotification.message) {
            setError("Vui lòng nhập đầy đủ tiêu đề và nội dung.");
            return;
        }
        try {
            await axios.post("http://localhost:5000/notifications/notifications", newNotification);
            setNewNotification({ title: "", message: "", recipient: "all" });
            setError(""); // Xóa lỗi nếu có
            fetchNotifications();
        } catch (error) {
            console.error("❌ Lỗi khi tạo thông báo:", error);
            setError("Không thể tạo thông báo. Vui lòng thử lại!");
        }
    };

    return (
        <div style={{ maxWidth: "600px", margin: "0 auto", padding: "20px", fontFamily: "Arial, sans-serif" }}>
            <h2>📢 Quản lý Thông báo</h2>

            {/* Hiển thị lỗi nếu có */}
            {error && <p style={{ color: "red", fontWeight: "bold" }}>{error}</p>}

            {/* Form thêm thông báo */}
            <div style={{ background: "#f9f9f9", padding: "10px", borderRadius: "8px", marginBottom: "20px" }}>
                <input
                    type="text"
                    placeholder="Nhập tiêu đề..."
                    style={{ width: "100%", padding: "8px", marginBottom: "8px", borderRadius: "4px", border: "1px solid #ccc" }}
                    value={newNotification.title}
                    onChange={(e) => setNewNotification({ ...newNotification, title: e.target.value })}
                />
                <textarea
                    placeholder="Nhập nội dung..."
                    style={{ width: "100%", padding: "8px", borderRadius: "4px", border: "1px solid #ccc" }}
                    value={newNotification.message}
                    onChange={(e) => setNewNotification({ ...newNotification, message: e.target.value })}
                />
                <button
                    onClick={handleCreate}
                    style={{ background: "#007bff", color: "white", padding: "8px 12px", borderRadius: "4px", border: "none", cursor: "pointer", marginTop: "10px" }}
                >
                    ➕ Thêm Thông báo
                </button>
            </div>

            {/* Danh sách thông báo */}
            <ul style={{ listStyle: "none", padding: 0 }}>
                {notifications.length === 0 ? (
                    <p style={{ color: "#888" }}>Không có thông báo nào.</p>
                ) : (
                    notifications.map((notification) => (
                        <li key={notification.id} style={{ padding: "10px", border: "1px solid #ddd", borderRadius: "5px", marginBottom: "10px" }}>
                            <h4 style={{ margin: "0 0 5px 0" }}>{notification.title}</h4>
                            <p style={{ margin: 0 }}>{notification.message}</p>
                            <button
                                onClick={() => handleDelete(notification.id)}
                                style={{ color: "red", background: "none", border: "none", cursor: "pointer", marginTop: "5px" }}
                            >
                                ❌ Xóa
                            </button>
                        </li>
                    ))
                )}
            </ul>
        </div>
    );
};

export default Notifications;
