import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const Notifications = () => {
    const [notifications, setNotifications] = useState([]);
    const [userRole, setUserRole] = useState(""); // Vai trò của người dùng
    const [newNotification, setNewNotification] = useState({ title: "", message: "", recipient: "users" });

    useEffect(() => {
        fetchUserRole(); // Lấy vai trò từ localStorage
    }, []);

    // Lấy vai trò của người dùng từ localStorage
    const fetchUserRole = () => {
        const user = JSON.parse(localStorage.getItem("user"));
        if (user && user.role) {
            setUserRole(user.role);
            fetchNotifications(user.role); // Lấy thông báo khi đã có vai trò người dùng
        } else {
            console.error("Không tìm thấy vai trò người dùng.");
        }
    };

    const fetchNotifications = async () => {
        try {
            const token = localStorage.getItem("token");
            if (!token) {
                console.error("Token không tồn tại, vui lòng đăng nhập lại.");
                // Thực hiện chuyển hướng về trang đăng nhập
                navigate("/login");
                return;
            }
            const userRole = localStorage.getItem("role");

            const response = await axios.get("http://localhost:5000/notifications/notifications", {
                headers: {
                    Authorization: `Bearer ${token}`,  // Thêm token vào header để xác thực
                    "User-Role": userRole,
                }
            });

            console.log(response.data); // Kiểm tra dữ liệu trả về từ server
            setNotifications(response.data);
        } catch (error) {
            console.error("Lỗi khi lấy danh sách thông báo:", error.response?.data || error.message);
        }
    };

    // Lọc thông báo theo vai trò
    const filteredNotifications = notifications.filter(notification =>
        notification.recipient === "users" || notification.recipient === userRole
    );

    // Xóa thông báo (chỉ cho Admin và Quản lý)
    const handleDelete = async (id) => {
        if (userRole !== "ad" && userRole !== "cm") {
            alert("Bạn không có quyền xóa thông báo.");
            return;
        }
        try {
            await axios.delete(`http://localhost:5000/notifications/notifications/${id}`, {
                headers: {
                    Authorization: `Bearer ${localStorage.getItem("token")}` // Thêm token khi xóa thông báo
                }
            });
            // Cập nhật lại danh sách thông báo sau khi xóa
            setNotifications(notifications.filter(notification => notification.id !== id));
        } catch (error) {
            console.error("Lỗi khi xóa thông báo:", error);
        }
    };

    // Tạo thông báo (chỉ cho Admin và Quản lý)
    const handleCreate = async () => {
        if (userRole !== "ad" && userRole !== "cm") {
            alert("Bạn không có quyền tạo thông báo.");
            return;
        }
        if (!newNotification.title || !newNotification.message) {
            alert("Vui lòng nhập tiêu đề và nội dung.");
            return;
        }
        try {
            await axios.post(
                "http://localhost:5000/notifications/notifications",
                {
                    title: newNotification.title,
                    message: newNotification.message,
                    recipient: newNotification.recipient,
                },
                {
                    headers: {
                        "Content-Type": "application/json",
                        Authorization: `Bearer ${localStorage.getItem("token")}` // Thêm token khi tạo thông báo
                    }
                }
            );
            setNewNotification({ title: "", message: "", recipient: "users" });
            fetchNotifications(userRole); // Cập nhật lại thông báo sau khi tạo mới
        } catch (error) {
            console.error("Lỗi tạo thông báo:", error);
            alert("Lỗi tạo thông báo: " + (error.response?.data?.message || error.message));
        }
    };

    return (
        <div>
            <h2>Thông báo</h2>

            {/* Chỉ Admin & Quản lý mới thấy phần tạo thông báo */}
            {(userRole === "ad" || userRole === "cm") && (
                <div>
                    <input
                        type="text"
                        placeholder="Tiêu đề"
                        value={newNotification.title}
                        onChange={(e) => setNewNotification({ ...newNotification, title: e.target.value })}
                    />
                    <textarea
                        placeholder="Nội dung"
                        value={newNotification.message}
                        onChange={(e) => setNewNotification({ ...newNotification, message: e.target.value })}
                    />
                    <select
                        value={newNotification.recipient}
                        onChange={(e) => setNewNotification({ ...newNotification, recipient: e.target.value })}
                    >
                        <option value="users">Tất cả người dùng</option>
                        <option value="hv">Học viên</option>
                        <option value="gv">Giáo viên</option>
                        <option value="cm">Quản lý</option>
                        <option value="ad">Admin</option>
                    </select>
                    <button onClick={handleCreate}>Thêm thông báo</button>
                </div>
            )}

            {/* Danh sách thông báo hiển thị */}
            <ul>
                {filteredNotifications.map((notification) => (
                    <li key={notification.id}>
                        <h4>{notification.title} ({notification.recipient})</h4>
                        <p>{notification.message}</p>

                        {/* Chỉ Admin & Quản lý có thể xóa thông báo */}
                        {(userRole === "ad" || userRole === "cm") && (
                            <button onClick={() => handleDelete(notification.id)}>Xóa</button>
                        )}
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default Notifications;
