import { useState, useEffect } from 'react';

function AttendanceAlert() {
    const [students, setStudents] = useState([]);

    useEffect(() => {
        // Giả lập lấy dữ liệu từ API
        const mockStudents = [
            { id: 1, name: 'Nguyễn Văn A', absentDays: 6 },
            { id: 2, name: 'Trần Thị B', absentDays: 2 },
            { id: 3, name: 'Lê Văn C', absentDays: 0 },
            { id: 4, name: 'Phạm Thị D', absentDays: 7 },
        ];
        setStudents(mockStudents);
    }, []);

    const alertAbsentees = () => {
        const absentees = students.filter(student => student.absentDays > 5);
        console.log('Học sinh nghỉ quá 5 buổi:', absentees);
    };

    return (
        <div>
            <h2>Báo cáo chuyên cần</h2>
            <button onClick={alertAbsentees}>Kiểm tra học sinh nghỉ quá 5 buổi</button>
            <table>
                <thead>
                    <tr>
                        <th>Học sinh</th>
                        <th>Số ngày nghỉ</th>
                    </tr>
                </thead>
                <tbody>
                    {students.filter(student => student.absentDays > 5).map((student, index) => (
                        <tr key={index}>
                            <td>{student.name}</td>
                            <td>{student.absentDays}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
}

export default AttendanceAlert;
