import { useEffect, useState } from "react";
import axios from "axios";
import "./Documents.css";

const Documents = ({ user }) => {
    const [classes, setClasses] = useState([]);
    const [selectedClass, setSelectedClass] = useState(null);
    const [documents, setDocuments] = useState([]);

    useEffect(() => {
        if (user) {
            fetchClasses(user.id, user.role);
        }
    }, [user]);

    const fetchClasses = async (userId, role) => {
        try {
            const endpoint = role === "gv" ? `/gv/classes/${userId}` : `/hv/classes/${userId}`;
            const response = await axios.get(`http://localhost:5000${endpoint}`);
            setClasses(response.data);
        } catch (error) {
            console.error("Lỗi khi lấy danh sách lớp:", error);
        }
    };

    const fetchDocuments = async (classId) => {
        try {
            const response = await axios.get(`http://localhost:5000/classes/${classId}/documents`);
            setDocuments(response.data);
            setSelectedClass(classId);
        } catch (error) {
            console.error("Lỗi khi lấy tài liệu:", error);
        }
    };

    return (
        <div className="documents-container">
            <h2>Tài liệu học tập</h2>
            <div className="class-list">
                {classes.map((cls) => (
                    <button
                        key={cls.id}
                        className={selectedClass === cls.id ? "active" : ""}
                        onClick={() => fetchDocuments(cls.id)}
                    >
                        {cls.name}
                    </button>
                ))}
            </div>
            <div className="document-list">
                {documents.length === 0 ? (
                    <p>Chưa có tài liệu nào.</p>
                ) : (
                    <ul>
                        {documents.map((doc) => (
                            <li key={doc.id}>
                                <a href={doc.url} target="_blank" rel="noopener noreferrer">{doc.title}</a>
                            </li>
                        ))}
                    </ul>
                )}
            </div>
        </div>
    );
};

export default Documents;
