import { useState } from 'react';
import './Schedule.css';

function Schedule() {
    const [scheduleData, setScheduleData] = useState([
        { day: 'Monday', time: '8:00 AM - 10:00 AM', subject: 'Math', teacher: 'Mr. A' },
        { day: 'Tuesday', time: '10:00 AM - 12:00 PM', subject: 'English', teacher: 'Mr. B' },
        { day: 'Wednesday', time: '1:00 PM - 3:00 PM', subject: 'Science', teacher: 'Mr. C' },
    ]);

    const [newSchedule, setNewSchedule] = useState({
        day: '',
        time: '',
        subject: '',
        teacher: ''
    });

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setNewSchedule(prevState => ({
            ...prevState,
            [name]: value
        }));
    };

    const handleAddSchedule = () => {
        if (newSchedule.day && newSchedule.time && newSchedule.subject && newSchedule.teacher) {
            setScheduleData(prevData => [...prevData, newSchedule]);
            setNewSchedule({ day: '', time: '', subject: '', teacher: '' });
        }
    };

    const handleDeleteSchedule = (index) => {
        const updatedSchedule = scheduleData.filter((_, idx) => idx !== index);
        setScheduleData(updatedSchedule);
    };

    return (
        <div className="schedule">


            <div className="add-schedule-form">
                <h2>Thêm Lịch Học</h2>
                <div className="form-row">
                    <input
                        type="text"
                        name="day"
                        value={newSchedule.day}
                        onChange={handleInputChange}
                        placeholder="Ngày"
                    />
                    <input
                        type="text"
                        name="time"
                        value={newSchedule.time}
                        onChange={handleInputChange}
                        placeholder="Giờ"
                    />
                    <input
                        type="text"
                        name="subject"
                        value={newSchedule.subject}
                        onChange={handleInputChange}
                        placeholder="Môn học"
                    />
                    <input
                        type="text"
                        name="teacher"
                        value={newSchedule.teacher}
                        onChange={handleInputChange}
                        placeholder="Giáo viên"
                    />
                    <button onClick={handleAddSchedule}>Thêm Lịch Học</button>
                </div>
            </div>

            <div className="schedule-list">
                <h2>Danh Sách Lịch Học</h2>
                <table>
                    <thead>
                        <tr>
                            <th>Ngày</th>
                            <th>Giờ</th>
                            <th>Môn học</th>
                            <th>Giáo viên</th>
                            <th>Hành động</th>
                        </tr>
                    </thead>
                    <tbody>
                        {scheduleData.map((item, index) => (
                            <tr key={index}>
                                <td>{item.day}</td>
                                <td>{item.time}</td>
                                <td>{item.subject}</td>
                                <td>{item.teacher}</td>
                                <td>
                                    <button onClick={() => handleDeleteSchedule(index)}>Xóa</button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </div>
    );
}

export default Schedule;
