import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { io } from "socket.io-client";
import { FaHome, FaBell, FaCog, FaUser, FaSignOutAlt } from "react-icons/fa";
import "./Home.css";
import Revenue from "./Revenue";

const socket = io("http://localhost:5000"); // K·∫øt n·ªëi WebSocket

const Home = ({ user, token }) => {
    const navigate = useNavigate();
    const [currentUser, setCurrentUser] = useState(user || JSON.parse(localStorage.getItem("user")));
    const [showSettings, setShowSettings] = useState(false);
    const [notifications, setNotifications] = useState([]);
    const [showNotifications, setShowNotifications] = useState(false);
    const [unreadCount, setUnreadCount] = useState(0); // S·ªë l∆∞·ª£ng th√¥ng b√°o ch∆∞a ƒë·ªçc

    useEffect(() => {
        const storedUser = JSON.parse(localStorage.getItem("user"));
        if (!storedUser) {
            navigate("/login");
        } else {
            setCurrentUser(storedUser);
        }

        // K·∫øt n·ªëi WebSocket v√† nh·∫≠n th√¥ng b√°o m·ªõi
        socket.emit("join", { userId: storedUser.id });

        socket.on("newNotification", (notification) => {
            setNotifications((prev) => [notification, ...prev]);
            setUnreadCount((prev) => prev + 1); // C·∫≠p nh·∫≠t s·ªë l∆∞·ª£ng ch∆∞a ƒë·ªçc
        });

        return () => {
            socket.off("newNotification");
        };
    }, [navigate]);

    const handleLogout = () => {
        localStorage.removeItem("user");
        setCurrentUser(null);
        navigate("/login");
    };

    if (!currentUser) return <p>ƒêang t·∫£i...</p>;

    const toggleNotifications = () => {
        setShowNotifications(!showNotifications);
        if (!showNotifications) setUnreadCount(0); // ƒê·∫∑t l·∫°i s·ªë th√¥ng b√°o ch∆∞a ƒë·ªçc khi m·ªü danh s√°ch
    };

    return (
        <div className="home-container">
            <nav className="navbar">
                <h1>Trung T√¢m Gia S∆∞</h1>
                <div className="navbar-buttons">
                    <span className="nav-item" onClick={() => navigate("/home")}><FaHome /> Trang ch·ªß</span>

                    {/* N√∫t chu√¥ng th√¥ng b√°o */}
                    <span className="nav-item notification-button" onClick={toggleNotifications}>
                        <FaBell />
                        {unreadCount > 0 && <span className="notification-badge">{unreadCount}</span>}
                        Th√¥ng b√°o
                    </span>

                    <div className="settings-container">
                        <span className="nav-item" onClick={() => setShowSettings(!showSettings)}>
                            <FaCog /> C√†i ƒë·∫∑t
                        </span>
                        {showSettings && (
                            <div className="settings-menu">
                                <span onClick={() => navigate("/settings/profile")}>üîπ Th√¥ng tin c√° nh√¢n</span>
                                <span onClick={() => navigate("/settings/password")}>üîπ ƒê·ªïi m·∫≠t kh·∫©u</span>
                                <span onClick={() => navigate("/settings/preferences")}>üîπ T√πy ch·ªânh giao di·ªán</span>
                            </div>
                        )}
                    </div>

                    <span className="nav-item" onClick={() => navigate("/profile")}><FaUser /> Th√¥ng tin c√° nh√¢n</span>
                    <span className="nav-item logout" onClick={handleLogout}><FaSignOutAlt /> ƒêƒÉng xu·∫•t</span>
                </div>
            </nav>

            {/* Danh s√°ch th√¥ng b√°o */}
            {showNotifications && (
                <div className="notification-popup">
                    <h3>Th√¥ng b√°o m·ªõi</h3>
                    <ul>
                        {notifications.map((notif, index) => (
                            <li key={index}>{notif.message}</li>
                        ))}
                    </ul>
                </div>
            )}

            <div className="main-content">
                <main className="dashboard">
                    <section className="dashboard-content">
                        <h2>Th·ªëng k√™ doanh thu</h2>
                        <div className="chart-container">
                            <Revenue />
                        </div>
                    </section>
                </main>
            </div>
        </div>
    );
};

export default Home;
