import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { FaFolderOpen, FaFileAlt } from "react-icons/fa";
import "./Documents.css";

const Documents = ({ user }) => {
    const navigate = useNavigate();
    const [classes, setClasses] = useState([]);
    const [selectedClass, setSelectedClass] = useState(null);
    const [documents, setDocuments] = useState([]);

    useEffect(() => {
        if (!user) {
            navigate("/login");
            return;
        }
        fetchClasses();
    }, [user, navigate]);

    const fetchClasses = async () => {
        try {
            const response = await axios.get(`http://localhost:5000/${user.role}/classes/${user.id}`);
            setClasses(response.data);
        } catch (error) {
            console.error("Lỗi khi lấy danh sách lớp:", error);
        }
    };

    const fetchDocuments = async (classId) => {
        try {
            const response = await axios.get(`http://localhost:5000/classes/${classId}/documents`);
            setDocuments(response.data);
            setSelectedClass(classId);
        } catch (error) {
            console.error("Lỗi khi lấy tài liệu:", error);
        }
    };

    return (
        <div className="documents-container">
            <h2>Danh sách lớp</h2>
            <div className="class-list">
                {classes.map((classItem) => (
                    <div
                        key={classItem.id}
                        className="class-item"
                        onClick={() => fetchDocuments(classItem.id)}
                    >
                        <FaFolderOpen /> {classItem.name}
                    </div>
                ))}
            </div>
            {selectedClass && (
                <div className="documents-list">
                    <h3>Tài liệu lớp: {classes.find(cls => cls.id === selectedClass)?.name}</h3>
                    <ul>
                        {documents.map((doc) => (
                            <li key={doc.id} className="document-item">
                                <FaFileAlt /> <a href={doc.fileUrl} target="_blank" rel="noopener noreferrer">{doc.title}</a>
                            </li>
                        ))}
                    </ul>
                    {user.role === "gv" && (
                        <button className="upload-btn">Thêm tài liệu</button>
                    )}
                </div>
            )}
        </div>
    );
};

export default Documents;
