import React, { useEffect, useState } from "react";
import axios from "axios";
import "./ManageGroups.css"; // ✅ Import file CSS

const ManageGroups = () => {
    const [groups, setGroups] = useState([]);
    const [newGroup, setNewGroup] = useState({
        group_name: "",
        grade_level: "",
        level: "",
        schedule: ""
    });
    const [message, setMessage] = useState(""); // ✅ Thêm state cho thông báo
    const [loading, setLoading] = useState(false); // ✅ Thêm state loading

    useEffect(() => {
        fetchGroups();
    }, []);

    const fetchGroups = async () => {
        try {
            const res = await axios.get("http://localhost:5000/manager/groups");
            setGroups(res.data);
        } catch (error) {
            console.error("Lỗi lấy danh sách nhóm học:", error);
        }
    };

    const addGroup = async () => {
        if (!newGroup.group_name || !newGroup.grade_level || !newGroup.level) {
            setMessage("⚠️ Vui lòng nhập đầy đủ thông tin!");
            return;
        }

        setLoading(true); // ✅ Bắt đầu loading
        try {
            const res = await axios.post("http://localhost:5000/manager/groups", newGroup);
            setNewGroup({ group_name: "", grade_level: "", level: "", schedule: "" });

            // ✅ Cập nhật UI ngay thay vì fetch lại từ server
            setGroups((prevGroups) => [...prevGroups, { id: res.data.id, ...newGroup }]);

            setMessage("✅ Nhóm học đã được thêm thành công!");
        } catch (error) {
            console.error("Lỗi khi thêm nhóm học:", error);
            setMessage("❌ Lỗi khi thêm nhóm học!");
        } finally {
            setLoading(false); // ✅ Kết thúc loading
        }
    };

    const deleteGroup = async (id) => {
        try {
            await axios.delete(`http://localhost:5000/manager/groups/${id}`);
            setGroups((prevGroups) => prevGroups.filter((group) => group.id !== id));
            setMessage("🗑️ Nhóm học đã được xóa!");
        } catch (error) {
            console.error("Lỗi khi xóa nhóm học:", error);
            setMessage("❌ Lỗi khi xóa nhóm học!");
        }
    };

    return (
        <div className="container">
            <h2>📖 Quản lý nhóm học</h2>

            {/* ✅ Hiển thị thông báo */}
            {message && <p className="message">{message}</p>}

            <div className="input-group">
                <input
                    type="text"
                    value={newGroup.group_name}
                    onChange={(e) => setNewGroup({ ...newGroup, group_name: e.target.value })}
                    placeholder="Tên nhóm"
                />
                <input
                    type="text"
                    value={newGroup.grade_level}
                    onChange={(e) => setNewGroup({ ...newGroup, grade_level: e.target.value })}
                    placeholder="Cấp độ (Ví dụ: Cấp 1, Cấp 2...)"
                />
                <input
                    type="text"
                    value={newGroup.level}
                    onChange={(e) => setNewGroup({ ...newGroup, level: e.target.value })}
                    placeholder="Trình độ (Cơ bản, Trung bình, Nâng cao...)"
                />
                <input
                    type="text"
                    value={newGroup.schedule}
                    onChange={(e) => setNewGroup({ ...newGroup, schedule: e.target.value })}
                    placeholder="Lịch học (Thứ 2 - Thứ 4 - Thứ 6)"
                />

                {/* ✅ Hiển thị loading khi đang thêm */}
                <button onClick={addGroup} disabled={loading}>
                    {loading ? "Đang thêm..." : "➕ Thêm nhóm"}
                </button>
            </div>

            <ul>
                {groups.map((group) => (
                    <li key={group.id}>
                        <span>
                            <strong>{group.group_name}</strong> ({group.grade_level} - {group.level})<br />
                            📅 {group.schedule || "Chưa có lịch"}
                        </span>
                        <button onClick={() => deleteGroup(group.id)}>🗑️ Xóa</button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default ManageGroups;
