import React, { useEffect, useState } from "react";
import axios from "axios";
import "./ManageGroups.css"; // ‚úÖ Import file CSS

const ManageGroups = () => {
    const [groups, setGroups] = useState([]);
    const [newGroup, setNewGroup] = useState({
        group_name: "",
        grade_level: "",
        level: "",
        schedule: ""
    });
    const [message, setMessage] = useState(""); // ‚úÖ Th√™m state cho th√¥ng b√°o
    const [loading, setLoading] = useState(false); // ‚úÖ Th√™m state loading

    useEffect(() => {
        fetchGroups();
    }, []);

    const fetchGroups = async () => {
        try {
            const res = await axios.get("http://localhost:5000/manager/groups");
            setGroups(res.data);
        } catch (error) {
            console.error("L·ªói l·∫•y danh s√°ch nh√≥m h·ªçc:", error);
        }
    };

    const addGroup = async () => {
        if (!newGroup.group_name || !newGroup.grade_level || !newGroup.level) {
            setMessage("‚ö†Ô∏è Vui l√≤ng nh·∫≠p ƒë·∫ßy ƒë·ªß th√¥ng tin!");
            return;
        }

        setLoading(true); // ‚úÖ B·∫Øt ƒë·∫ßu loading
        try {
            const res = await axios.post("http://localhost:5000/manager/groups", newGroup);
            setNewGroup({ group_name: "", grade_level: "", level: "", schedule: "" });

            // ‚úÖ C·∫≠p nh·∫≠t UI ngay thay v√¨ fetch l·∫°i t·ª´ server
            setGroups((prevGroups) => [...prevGroups, { id: res.data.id, ...newGroup }]);

