        } catch (error) {
            console.error("L·ªói khi th√™m nh√≥m h·ªçc:", error);
            setMessage("‚ùå L·ªói khi th√™m nh√≥m h·ªçc!");
        } finally {
            setLoading(false); // ‚úÖ K·∫øt th√∫c loading
        }
