// frontend (React)

import React, { useState, useEffect } from 'react';

const MakeUpClasses = ({ studentId }) => {
    const [makeUpClasses, setMakeUpClasses] = useState([]);

    useEffect(() => {
        // Fetch lớp học bù từ API
        fetch(`http://localhost:5000/student/make-up-classes/${studentId}`)
            .then(response => response.json())
            .then(data => setMakeUpClasses(data))
            .catch(error => console.error('Error fetching data:', error));
    }, [studentId]);

    return (
        <div>
            <h3>Danh sách lớp học bù của bạn</h3>
            {makeUpClasses.length > 0 ? (
                <ul>
                    {makeUpClasses.map((makeUpClass, index) => (
                        <li key={index}>
                            <strong>{makeUpClass.class_name}</strong> - Ngày học bù: {new Date(makeUpClass.make_up_date).toLocaleDateString()}
                        </li>
                    ))}
                </ul>
            ) : (
                <p>Không có lớp học bù nào.</p>
            )}
        </div>
    );
};

export default MakeUpClasses;
