import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "./Grades.css";

const Grades = ({ user }) => {
    const navigate = useNavigate();
    const [classes, setClasses] = useState([]);
    const [selectedClass, setSelectedClass] = useState(null); // L∆∞u c·∫£ th√¥ng tin l·ªõp h·ªçc
    const [grades, setGrades] = useState([]);
    const [loading, setLoading] = useState(false);

    useEffect(() => {
        if (!user) {
            const storedUser = JSON.parse(localStorage.getItem("user"));
            if (!storedUser) {
                navigate("/login");
                return;
            }
            fetchClasses(storedUser.id);
        } else {
            fetchClasses(user.id);
        }
    }, [user, navigate]);

    const fetchClasses = async (userId) => {
        try {
            const response = await fetch(`http://localhost:5000/student/registered-classes/${userId}`);
            if (!response.ok) throw new Error("L·ªói khi l·∫•y danh s√°ch l·ªõp h·ªçc.");
            const data = await response.json();
            setClasses(data);
        } catch (error) {
            console.error("‚ùå L·ªói:", error);
        }
    };

    const fetchGrades = async (userId, cls) => {
        setLoading(true);
        setSelectedClass(cls); // L∆∞u l·∫°i th√¥ng tin l·ªõp h·ªçc ƒëang xem ƒëi·ªÉm
        try {
            const response = await fetch(`http://localhost:5000/student/grades/${userId}/${cls.class_id}`);
            if (!response.ok) throw new Error("L·ªói khi l·∫•y ƒëi·ªÉm s·ªë.");
            const data = await response.json();
            setGrades(data);
        } catch (error) {
            console.error("‚ùå L·ªói:", error);
            setGrades([]);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="grades-container">
            <h2>üìö Danh s√°ch l·ªõp h·ªçc c·ªßa b·∫°n</h2>

            {classes.length === 0 ? (
                <p>‚ùå B·∫°n ch∆∞a ƒëƒÉng k√Ω l·ªõp h·ªçc n√†o.</p>
            ) : (
                <div className="class-list">
                    {classes.map((cls) => (
                        <button
                            key={cls.class_id}
                            className={cls.class_id === selectedClass?.class_id ? "selected" : ""}
                            onClick={() => fetchGrades(user.id, cls)}
                        >
                            {cls.class_name} ({cls.subject})
                        </button>
                    ))}
                </div>
            )}

            {selectedClass && (
                <>
                    <h2>üìä ƒêi·ªÉm s·ªë c·ªßa l·ªõp {selectedClass.class_name}</h2>
                    {loading ? (
                        <p>ƒêang t·∫£i...</p>
                    ) : grades.length === 0 ? (
                        <p>üìå Ch∆∞a c√≥ ƒëi·ªÉm.</p>
                    ) : (
                        <table className="grades-table">
                            <thead>
                                <tr>
                                    <th>üìù T√™n b√†i ki·ªÉm tra</th>
                                    <th>üìä ƒêi·ªÉm s·ªë</th>
                                    <th>üìÖ Ng√†y thi</th>
                                </tr>
                            </thead>
                            <tbody>
                                {grades.map((grade, index) => (
                                    <tr key={index}>
                                        <td>{grade.exam_name}</td>
                                        <td>{grade.score}</td>
                                        <td>{new Date(grade.exam_date).toLocaleDateString()}</td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    )}
                </>
            )}
        </div>
    );
};

export default Grades;
