import React, { useEffect, useState } from "react";
import axios from "axios";

const ClassRegistration = () => {
    const [pendingFees, setPendingFees] = useState([]);
    const [confirmedFees, setConfirmedFees] = useState([]);

    useEffect(() => {
        // Lấy danh sách nhóm chưa xác nhận học phí
        axios.get("http://localhost:5000/student/unconfirmed-registrations")
            .then(response => setPendingFees(response.data))
            .catch(error => console.error("Lỗi khi lấy danh sách chưa xác nhận học phí:", error));

        // Lấy danh sách nhóm đã xác nhận nhưng chưa thanh toán
        axios.get("http://localhost:5000/student/unpaid-fees")
            .then(response => setConfirmedFees(response.data))
            .catch(error => console.error("Lỗi khi lấy danh sách đã xác nhận nhưng chưa thanh toán:", error));
    }, []);

    return (
        <div>
            <h2>Nhóm Chưa Xác Nhận Học Phí</h2>
            <table>
                <thead>
                    <tr>
                        <th>Tên Lớp</th>
                        <th>Số Học Viên</th>
                    </tr>
                </thead>
                <tbody>
                    {pendingFees.map((group, index) => (
                        <tr key={index}>
                            <td>{group.class_name}</td>
                            <td>{group.student_count}</td>
                        </tr>
                    ))}
                </tbody>
            </table>

            <h2>Nhóm Đã Xác Nhận Nhưng Chưa Thanh Toán</h2>
            <table>
                <thead>
                    <tr>
                        <th>Tên Lớp</th>
                        <th>Số Học Viên</th>
                        <th>Học Phí</th>
                    </tr>
                </thead>
                <tbody>
                    {confirmedFees.map((group, index) => (
                        <tr key={index}>
                            <td>{group.class_name}</td>
                            <td>{group.student_count}</td>
                            <td>{group.amount} VNĐ</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default ClassRegistration;
