import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "./Fees.css";

const Payments = () => {
    const navigate = useNavigate();
    const [fees, setFees] = useState(null);  // Chỉnh về null để dễ check dữ liệu
    const [isLoading, setIsLoading] = useState(true);
    const [paymentMethod, setPaymentMethod] = useState("bank");
    const [showQR, setShowQR] = useState(false);
    const [user, setUser] = useState(null);

    useEffect(() => {
        const storedUser = JSON.parse(localStorage.getItem("user"));
        if (!storedUser) {
            navigate("/login");
        } else {
            setUser(storedUser);
            fetchFees(storedUser.id);
        }
    }, [navigate]);

    const fetchFees = async (studentId) => {
        setIsLoading(true);
        try {
            const response = await fetch(`http://localhost:5000/student/fees/${studentId}?timestamp=${new Date().getTime()}`);
            if (!response.ok) throw new Error("Không thể lấy dữ liệu học phí.");
            const data = await response.json();

            console.log("📊 Dữ liệu học phí từ API sau khi fetch:", data);

            setFees(data[0]);
        } catch (error) {
            console.error("❌ Lỗi lấy dữ liệu học phí:", error);
            setFees(null);
        }
        setIsLoading(false);
    };

    const handlePayment = async () => {
        if (!fees || fees.remaining_unpaid === 0) {
            alert("❌ Không có khoản nào cần thanh toán.");
            return;
        }

        // Lấy danh sách lớp chưa thanh toán
        const unpaidClasses = fees.unpaid_groups.split(",");

        const paymentData = {
            studentId: user?.id,
            amount: fees.remaining_unpaid,
            classIds: unpaidClasses, // Gửi danh sách các class_id cần thanh toán
        };

        console.log("💰 Dữ liệu thanh toán gửi đi:", paymentData);

        try {
            const res = await fetch("http://localhost:5000/student/pay", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify(paymentData),
            });

            const result = await res.json();
            console.log("🔄 Kết quả thanh toán:", result);
            if (result.success) {
                fetchFees(user.id); // Cập nhật lại dữ liệu học phí
            } else {
                alert("⚠️ Thanh toán thất bại, vui lòng thử lại.");
            }
        } catch (error) {
            console.error("❌ Lỗi khi gửi yêu cầu thanh toán:", error);
        }

    };

    if (isLoading) {
        return <p>🔄 Đang tải dữ liệu...</p>;
    }

    if (!fees) {
        return <p>❌ Không có dữ liệu học phí.</p>;
    }

    return (
        <div className="fees-container">
            <h2>💳 Thanh toán học phí</h2>

            <div className="fee-summary">
                <h3>📚 Lớp đã thanh toán</h3>
                <p>{fees.paid_groups || "Không có lớp nào đã thanh toán."}</p>
                <p><strong>Tổng số lớp:</strong> {fees.paid_group_count}</p>
                <p><strong>Tổng học phí đã đóng:</strong> {fees.total_paid_fee.toLocaleString()} VNĐ</p>
                <p><strong>Ngày đóng gần nhất:</strong> {fees.last_payment_date ? new Date(fees.last_payment_date).toLocaleDateString() : "Chưa đóng"}</p>
            </div>

            <div className="fee-summary unpaid">
                <h3>💸 Lớp chưa thanh toán</h3>
                <p>{fees.unpaid_groups || "Không có lớp nào cần thanh toán."}</p>
                <p><strong>Tổng số lớp:</strong> {fees.unpaid_group_count}</p>
                <p><strong>Tổng học phí cần thanh toán:</strong> {fees.total_unpaid_fee.toLocaleString()} VNĐ</p>
                <p><strong>Còn lại:</strong> <span style={{ color: "red", fontWeight: "bold" }}>{fees.remaining_unpaid.toLocaleString()} VNĐ</span></p>
            </div>

            {/* 🔥 Khu vực thanh toán */}
            {fees.remaining_unpaid > 0 && (
                <div className="payment-section">
                    <h3>🔹 Khu vực thanh toán</h3>
                    <p>Tổng số tiền cần thanh toán: <strong>{fees.remaining_unpaid.toLocaleString()} VNĐ</strong></p>

                    <label>Chọn phương thức thanh toán:</label>
                    <select value={paymentMethod} onChange={(e) => setPaymentMethod(e.target.value)}>
                        <option value="bank">💳 Chuyển khoản</option>
                        <option value="cash">💵 Tiền mặt</option>
                    </select>

                    <button className="pay-button" onClick={handlePayment}>Xác nhận thanh toán</button>

                    <button className="qr-button" onClick={() => setShowQR(!showQR)}>
                        {showQR ? "Ẩn QR Code" : "🔍 Hiển thị QR Code"}
                    </button>

                    {showQR && (
                        <div className="qr-container">
                            <h3>📌 Nộp học phí qua Momo</h3>
                            <p><strong>Ngân hàng:</strong> Techcombank</p>
                            <p><strong>Mã giao dịch:</strong> CTUB{user.id}1741590453</p>
                            <p><strong>Mô tả:</strong> HK2, 2024-2025, MSSV{user.id}, Số tiền {fees.remaining_unpaid.toLocaleString()} VNĐ</p>
                            <img src="/images/z6406292555094_d67ab8e1e253bc02c6373ff7079c1307.jpg" alt="QR Momo" width="200" />
                        </div>
                    )}
                </div>
            )}
        </div>
    );
};

export default Payments;
