import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import DraggablePopup from "./DraggablePopup"; // Import DraggablePopup
import "./Profile.css";

const Profile = () => {
    const navigate = useNavigate();
    const [user, setUser] = useState(null);
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [popupMessage, setPopupMessage] = useState(""); // State for popup message
    const [isPopupOpen, setIsPopupOpen] = useState(false); // State for popup visibility
    const [oldPassword, setOldPassword] = useState("");
    const [newPassword, setNewPassword] = useState("");
    const [confirmPassword, setConfirmPassword] = useState("");
    useEffect(() => {
        const storedUser = JSON.parse(localStorage.getItem("user"));
        if (!storedUser) {
            navigate("/login");
        } else {
            setUser(storedUser);
        }
    }, [navigate]);

    // X·ª≠ l√Ω thay ƒë·ªïi m·∫≠t kh·∫©u
    const handleChangePassword = async () => {
        if (newPassword !== confirmPassword) {
            alert("M·∫≠t kh·∫©u m·ªõi kh√¥ng kh·ªõp!");
            return;
        }

        const storedUser = JSON.parse(localStorage.getItem("user"));
        if (!storedUser) {
            alert("L·ªói: Kh√¥ng t√¨m th·∫•y th√¥ng tin ng∆∞·ªùi d√πng!");
            return;
        }

        try {
            const response = await fetch("http://localhost:5000/change-password", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({
                    userId: storedUser.id,
                    oldPassword,
                    newPassword,
                }),
            });

            const data = await response.json();
            if (response.ok) {
                alert("ƒê·ªïi m·∫≠t kh·∫©u th√†nh c√¥ng!");
                closeModal();  // ƒê√≥ng modal khi th√†nh c√¥ng
            } else {
                alert(data.error || "C√≥ l·ªói x·∫£y ra!");
            }
        } catch (error) {
            console.error("L·ªói khi ƒë·ªïi m·∫≠t kh·∫©u:", error);
            alert("ƒê√£ x·∫£y ra l·ªói khi ƒë·ªïi m·∫≠t kh·∫©u.");
        }
    };

    return (
        <div className="profile-container">
            <h2>üë§ Th√¥ng tin c√° nh√¢n</h2>
            <div className="profile-card">
                <p><strong>üë§ H·ªç v√† t√™n:</strong> {user.fullName}</p>
                <p><strong>üìß Email:</strong> {user.email}</p>
                <p><strong>üìû S·ªë ƒëi·ªán tho·∫°i:</strong> {user.phone || "Ch∆∞a c·∫≠p nh·∫≠t"}</p>
                <p><strong>üé≠ Vai tr√≤:</strong> {user.role.toUpperCase()}</p>
            </div>

            <button onClick={() => setIsModalOpen(true)} className="change-password-btn">
                üîë ƒê·ªïi m·∫≠t kh·∫©u
