import { useEffect, useState } from "react";

const ManageTeachers = () => {
    const [teachers, setTeachers] = useState([]);
    const [fullName, setFullName] = useState("");
    const [username, setUsername] = useState("");
    const [email, setEmail] = useState("");
    const [phone, setPhone] = useState("");
    const [password, setPassword] = useState("");

    useEffect(() => {
        fetchTeachers();
    }, []);

    const fetchTeachers = () => {
        fetch("http://localhost:5000/manager/teachers")
            .then((response) => response.json())
            .then((data) => setTeachers(data))
            .catch((error) => console.error("Lỗi khi lấy giáo viên:", error));
    };

    const handleAddTeacher = async (e) => {
        e.preventDefault();

        const newTeacher = { fullName, username, email, phone, password };

        console.log("📤 Gửi dữ liệu lên server:", newTeacher); // Kiểm tra trước khi gửi

        try {
            const response = await fetch("http://localhost:5000/manager/teachers", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify(newTeacher),
            });

            const data = await response.json();
            console.log("📩 Phản hồi từ server:", data);

            if (response.ok) {
                alert("Thêm giáo viên thành công!");
                fetchTeachers(); // Cập nhật danh sách
                setFullName("");
                setUsername("");
                setEmail("");
                setPhone("");
                setPassword("");
            } else {
                alert(data.error);
            }
        } catch (error) {
            console.error("❌ Lỗi khi thêm giáo viên:", error);
        }
    };

    return (
        <div>
            <h2>Quản lý giáo viên</h2>

            <form onSubmit={handleAddTeacher}>
                <input
                    type="text"
                    placeholder="Họ và tên"
                    value={fullName}
                    onChange={(e) => setFullName(e.target.value)}
                    required
                />
                <input
                    type="text"
                    placeholder="Tên đăng nhập"
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                    required
                />
                <input
                    type="email"
                    placeholder="Email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    required
                />
                <input
                    type="text"
                    placeholder="Số điện thoại"
                    value={phone}
                    onChange={(e) => setPhone(e.target.value)}
                    required
                />
                <input
                    type="password"
                    placeholder="Mật khẩu"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                />
                <button type="submit">Thêm giáo viên</button>
            </form>

            <table border="1">
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Họ và tên</th>
                        <th>Tên đăng nhập</th>
                        <th>Email</th>
                        <th>Số điện thoại</th>
                        <th>Ngày tạo</th>
                    </tr>
                </thead>
                <tbody>
                    {teachers.map((teacher) => (
                        <tr key={teacher.id}>
                            <td>{teacher.id}</td>
                            <td>{teacher.fullName}</td>
                            <td>{teacher.username}</td>
                            <td>{teacher.email}</td>
                            <td>{teacher.phone}</td>
                            <td>{new Date(teacher.created_at).toLocaleDateString()}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default ManageTeachers;
