import { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "./Register.css";
const Register = () => {
    const [formData, setFormData] = useState({
        username: "",
        email: "",
        password: "",
        confirmPassword: "",
    });

    const [error, setError] = useState("");
    const navigate = useNavigate();

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };
    const handleSubmit = async (e) => {
        e.preventDefault();
        setError("");

        // ✅ Kiểm tra username
        if (!/^[a-zA-Z0-9]{4,20}$/.test(formData.username)) {
            setError("Tên đăng nhập phải từ 4-20 ký tự, chỉ chứa chữ và số!");
            return;
        }

        // ✅ Kiểm tra email
        if (!/^[\w-.]+@([\w-]+\.)+[\w-]{2,4}$/.test(formData.email)) {
            setError("Email không hợp lệ!");
            return;
        }

        // ✅ Kiểm tra mật khẩu
        if (formData.password.length < 8) {
            setError("Mật khẩu phải có ít nhất 8 ký tự!");
            return;
        }
        if (!/[A-Z]/.test(formData.password) || !/[a-z]/.test(formData.password) || !/[0-9]/.test(formData.password)) {
            setError("Mật khẩu phải chứa ít nhất 1 chữ hoa, 1 chữ thường và 1 số!");
            return;
        }
        if (/\s/.test(formData.password)) {
            setError("Mật khẩu không được chứa khoảng trắng!");
            return;
        }

        // ✅ Kiểm tra xác nhận mật khẩu
        if (formData.password !== formData.confirmPassword) {
            setError("Mật khẩu xác nhận không khớp!");
            return;
        }

        try {
            const res = await axios.post("http://localhost:5000/register", {
                username: formData.username,
                email: formData.email,
                password: formData.password,
            });

            console.log("Phản hồi từ server:", res.data);
            alert(res.data.message);
            navigate("/login");
        } catch (err) {
            console.error("Lỗi từ server:", err.response);
            setError(err.response?.data?.message || "Đã xảy ra lỗi!");
        }
    };




    return (
        <div className="register-container">
            <h2>Đăng ký tài khoản</h2>
            {error && <p className="error">{error}</p>}
            <form onSubmit={handleSubmit}>
                <input type="text" name="username" placeholder="Tên đăng nhập" onChange={handleChange} required />
                <input type="email" name="email" placeholder="Email" onChange={handleChange} required />
                <input type="password" name="password" placeholder="Mật khẩu" onChange={handleChange} required />
                <input type="password" name="confirmPassword" placeholder="Xác nhận mật khẩu" onChange={handleChange} required />
                <button type="submit">Đăng ký</button>
            </form>
        </div>
    );
};

export default Register;
