import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import DraggablePopup from "./DraggablePopup"; // Import DraggablePopup
import "./Profile.css";
import "./ChangePasswordModal.css"; // Ensure you have this CSS for styling

const Profile = () => {
    const navigate = useNavigate();
    const [user, setUser] = useState(null);
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [popupMessage, setPopupMessage] = useState(""); // State for popup message
    const [isPopupOpen, setIsPopupOpen] = useState(false); // State for popup visibility
    const [oldPassword, setOldPassword] = useState(""); // Old password input state
    const [newPassword, setNewPassword] = useState(""); // New password input state
    const [confirmPassword, setConfirmPassword] = useState(""); // Confirm password input state

    useEffect(() => {
        const storedUser = JSON.parse(localStorage.getItem("user"));
        if (!storedUser) {
            navigate("/login");
        } else {
            setUser(storedUser);
        }
    }, [navigate]);

    const handlePasswordChange = async () => {
        if (newPassword !== confirmPassword) {
            setPopupMessage("M·∫≠t kh·∫©u m·ªõi kh√¥ng kh·ªõp!");
            setIsPopupOpen(true);
            return;
        }

        // Logic to check and update password in the backend
        try {
            const response = await fetch("http://localhost:5000/change-password", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({
                    userId: user.id, // Send userId to identify the user
                    oldPassword,
                    newPassword,
                }),
            });

            const data = await response.json();

            if (response.ok) {
                setPopupMessage("ƒê·ªïi m·∫≠t kh·∫©u th√†nh c√¥ng!");
                setIsPopupOpen(true);
                setIsModalOpen(false); // Close modal after success
            } else {
                setPopupMessage(data.error || "C√≥ l·ªói x·∫£y ra!");
                setIsPopupOpen(true);
            }
        } catch (error) {
            setPopupMessage("L·ªói khi ƒë·ªïi m·∫≠t kh·∫©u!");
            setIsPopupOpen(true);
            console.error("L·ªói khi ƒë·ªïi m·∫≠t kh·∫©u:", error);
        }
    };

    if (!user) {
        return <p>ƒêang t·∫£i...</p>;
    }

    return (
        <div className="profile-container">
            <h2>üë§ Th√¥ng tin c√° nh√¢n</h2>
            <div className="profile-card">
                <p><strong>üë§ H·ªç v√† t√™n:</strong> {user.fullName}</p>
                <p><strong>üìß Email:</strong> {user.email}</p>
                <p><strong>üìû S·ªë ƒëi·ªán tho·∫°i:</strong> {user.phone || "Ch∆∞a c·∫≠p nh·∫≠t"}</p>
                <p><strong>üé≠ Vai tr√≤:</strong> {user.role.toUpperCase()}</p>
            </div>

            <button onClick={() => setIsModalOpen(true)} className="change-password-btn">
                üîë ƒê·ªïi m·∫≠t kh·∫©u
            </button>

            <button onClick={() => navigate("/home")} className="back-btn">‚¨ÖÔ∏è Quay l·∫°i</button>

            {/* ƒê·ªïi m·∫≠t kh·∫©u s·∫Ω ƒë∆∞·ª£c hi·ªÉn th·ªã trong DraggablePopup */}
            {isModalOpen && (
                <DraggablePopup>
                    <div className="modal-header popup-header">
                        <h4>ƒê·ªïi m·∫≠t kh·∫©u</h4>
                    </div>
                    <div className="modal-body">
                        <input
                            type="password"
                            placeholder="M·∫≠t kh·∫©u c≈©"
                            value={oldPassword}
                            onChange={(e) => setOldPassword(e.target.value)}
                        />
                        <input
                            type="password"
                            placeholder="M·∫≠t kh·∫©u m·ªõi"
                            value={newPassword}
                            onChange={(e) => setNewPassword(e.target.value)}
                        />
                        <input
                            type="password"
                            placeholder="X√°c nh·∫≠n m·∫≠t kh·∫©u m·ªõi"
                            value={confirmPassword}
                            onChange={(e) => setConfirmPassword(e.target.value)}
                        />
                        <button onClick={handlePasswordChange}>ƒê·ªïi m·∫≠t kh·∫©u</button>
                    </div>
                </DraggablePopup>
            )}

            {/* Popup for feedback */}
            {isPopupOpen && <DraggablePopup>{popupMessage}</DraggablePopup>}

        </div>
    );
};

export default Profile;
