import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "./Fees.css";  // Import CSS file

const Fees = ({ user }) => {
    const navigate = useNavigate();
    const [studentFees, setStudentFees] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        if (!user) {
            const storedUser = JSON.parse(localStorage.getItem("user"));
            if (!storedUser) {
                navigate("/login");
                return;
            }
            fetchFees(storedUser.id);
        } else {
            fetchFees(user.id);
        }
    }, [user, navigate]);

    const fetchFees = async (studentId) => {
        try {
            const response = await fetch(`http://localhost:5000/student/fees/${studentId}`);
            if (!response.ok) throw new Error("L·ªói khi t·∫£i th√¥ng tin h·ªçc ph√≠");
            const data = await response.json();
            setStudentFees(data);
        } catch (error) {
            console.error(error);
            setStudentFees([]);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="fees-container">
            <h2>üí∞ Th√¥ng tin h·ªçc ph√≠</h2>
            {loading ? (
                <p className="loading">ƒêang t·∫£i...</p>
            ) : studentFees.length === 0 ? (
                <p>Ch∆∞a c√≥ d·ªØ li·ªáu h·ªçc ph√≠.</p>
            ) : (
                <table className="fees-table">
                    <thead>
                        <tr>
                            <th>üìö Kho·∫£n h·ªçc ph√≠</th>
                            <th>üíµ S·ªë ti·ªÅn</th>
                            <th>üìÖ Th·ªùi gian h·ªçc</th>
                            <th>üí≥ S·ªë ti·ªÅn ƒë√£ ƒë√≥ng</th>
                            <th>üí∏ S·ªë ti·ªÅn c√≤n thi·∫øu</th>
                        </tr>
                    </thead>
                    <tbody>
                        {studentFees.map((fee, index) => (
                            <tr key={index}>
                                <td>{fee.name}</td>
                                <td>{fee.fee.toLocaleString()} VND</td>
                                <td>{new Date(fee.start_at).toLocaleDateString()} - {new Date(fee.end_at).toLocaleDateString()}</td>
                                <td>{fee.already_pay.toLocaleString()} VND</td>
                                <td>{fee.remaining.toLocaleString()} VND</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            )}
        </div>
    );
};

export default Fees;
