import { useEffect, useState } from 'react';

function Grades() {
    const [grades, setGrades] = useState([]);
    const [userRole, setUserRole] = useState(null);

    // Lấy userRole từ localStorage
    useEffect(() => {
        const user = localStorage.getItem('user');
        if (user) {
            const parsedUser = JSON.parse(user);
            setUserRole(parsedUser.role); // Lưu trữ userRole
        }
    }, []);

    // Giả lập lấy điểm từ server
    useEffect(() => {
        const mockGrades = userRole === 'cm'
            ? [ // CM có thể xem và nhập điểm của tất cả học viên
                { subject: 'Toán', student: 'Nguyễn Văn A', score: 9.5 },
                { subject: 'Lý', student: 'Trần Thị B', score: 8.0 },
            ]
            : [ // GV chỉ có thể nhập điểm cho học viên trong lớp của mình
                { subject: 'Toán', student: 'Nguyễn Văn A', score: 9.5 },
            ];

        setGrades(mockGrades);
    }, [userRole]);

    if (!userRole) return <div>Đang tải...</div>;

    return (
        <div className="grades">
            <h2>Xem điểm</h2>
            <table>
                <thead>
                    <tr>
                        <th>Môn học</th>
                        <th>Tên học viên</th>
                        <th>Điểm</th>
                    </tr>
                </thead>
                <tbody>
                    {grades.map((grade, index) => (
                        <tr key={index}>
                            <td>{grade.subject}</td>
                            <td>{grade.student}</td>
                            <td>{grade.score}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
}

export default Grades;
