// src/components/TeacherSchedule.jsx
import React, { useState, useEffect } from "react";
import { fetchTeacherSchedule } from "../api";

const TeacherSchedule = ({ teacherId }) => {
    const [schedule, setSchedule] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const getSchedule = async () => {
            try {
                const response = await fetchTeacherSchedule(teacherId);
                setSchedule(response.data);
                setLoading(false);
            } catch (error) {
                console.error("Lỗi khi lấy lịch dạy:", error);
            }
        };

        getSchedule();
    }, [teacherId]);

    if (loading) return <div>Loading...</div>;

    return (
        <div>
            <h2>Lịch Dạy Của Giảng Viên</h2>
            <table>
                <thead>
                    <tr>
                        <th>Nhóm Học</th>
                        <th>Thời Gian</th>
                        <th>Ngày</th>
                        <th>Phòng</th>
                    </tr>
                </thead>
                <tbody>
                    {schedule.map((session, index) => (
                        <tr key={index}>
                            <td>{session.group_name}</td>
                            <td>{`${session.start_at} - ${session.end_at}`}</td>
                            <td>{session.date_of_week}</td>
                            <td>{session.classroom_name}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default TeacherSchedule;
