import { useEffect, useState } from "react";

const TeacherClassRegister = () => {
    const [classes, setClasses] = useState([]);
    const [errorMessage, setErrorMessage] = useState("");  // Thêm thông báo lỗi
    const teacherId = JSON.parse(localStorage.getItem("user"))?.id; // Lấy ID giáo viên từ localStorage
    const [teacherClasses, setTeacherClasses] = useState([]); // Lưu danh sách các lớp giáo viên đã dạy

    useEffect(() => {
        // Lấy danh sách các lớp chưa có giáo viên
        fetch("http://localhost:5000/teacher/classes/unassigned")
            .then((res) => res.json())
            .then((data) => setClasses(data))
            .catch((error) => console.error("❌ Lỗi khi tải danh sách lớp:", error));

        // Lấy danh sách các lớp mà giáo viên đã đăng ký
        fetch(`http://localhost:5000/teacher/classes/${teacherId}`)
            .then((res) => res.json())
            .then((data) => setTeacherClasses(data))
            .catch((error) => console.error("❌ Lỗi khi tải các lớp của giáo viên:", error));
    }, [teacherId]);
    const normalizeScheduleString = (scheduleStr) => {
        if (!scheduleStr) return [];
        return scheduleStr.split(",").map(s => s.trim().toLowerCase());
    };

    const handleRegisterClass = (classId, className, classSchedule) => {
        // Kiểm tra trùng lịch
        const newSchedule = normalizeScheduleString(classSchedule);

        const existingSchedules = teacherClasses.flatMap(cls =>
            normalizeScheduleString(cls.schedule)
        );

        const isConflict = newSchedule.some(sch => existingSchedules.includes(sch));

        if (isConflict) {
            setErrorMessage("❌ Lịch học này trùng với lớp bạn đã đăng ký.");
            return;
        }
        // Nếu không có trùng lịch, tiến hành đăng ký lớp
        fetch(`http://localhost:5000/teacher/classes/${classId}/assign-teacher`, {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({ teacher_id: teacherId }),
        })
            .then((res) => res.json())
            .then((data) => {
                alert(`✅ Bạn đã đăng ký dạy lớp ${className} thành công!`);
                setClasses(classes.filter((cls) => cls.id !== classId)); // Xóa lớp đã đăng ký khỏi danh sách
                setTeacherClasses([...teacherClasses, { schedule: classSchedule }]); // Thêm lớp vào danh sách lớp giáo viên đã dạy
            })
            .catch((error) => console.error("❌ Lỗi khi đăng ký lớp:", error));
    };

    return (
        <div>
            <h2>Danh sách lớp chưa có giáo viên</h2>
            {errorMessage && <div style={{ color: "red", marginBottom: "10px" }}>{errorMessage}</div>} {/* Hiển thị thông báo lỗi */}
            <table border="1" cellPadding="8" cellSpacing="0">
                <thead>
                    <tr>
                        <th>Tên lớp</th>
                        <th>Trình độ</th>
                        <th>Khối</th>
                        <th>Sĩ số</th>
                        <th>Lịch học</th>
                        <th>Hành động</th>
                    </tr>
                </thead>
                <tbody>
                    {classes.length > 0 ? (
                        classes.map((cls) => (
                            <tr key={cls.id}>
                                <td>{cls.name}</td>
                                <td>{cls.type_mapped}</td>
                                <td>{cls.grade}</td>
                                <td>{cls.current_student}/{cls.max_student}</td>
                                <td>{cls.schedule || "Chưa có lịch"}</td>
                                <td>
                                    <button onClick={() => handleRegisterClass(cls.id, cls.name, cls.schedule)}>
                                        Đăng ký dạy
                                    </button>
                                </td>
                            </tr>
                        ))
                    ) : (
                        <tr>
                            <td colSpan="6" style={{ textAlign: "center" }}>Không có lớp nào cần giáo viên.</td>
                        </tr>
                    )}
                </tbody>
            </table>
        </div>
    );
};

export default TeacherClassRegister;
