import React, { useEffect, useState } from 'react';
import axios from 'axios';
import jsPDF from 'jspdf';
import autoTable from 'jspdf-autotable';
import { saveAs } from 'file-saver';
import * as XLSX from 'xlsx';

const TuitionSalary = () => {
    const [revenues, setRevenues] = useState([]);
    const [filterDate, setFilterDate] = useState('');
    const [searchStudent, setSearchStudent] = useState('');
    const [totalRevenue, setTotalRevenue] = useState(0);
    const [totalStudents, setTotalStudents] = useState(0);

    useEffect(() => {
        fetchRevenueData();
    }, [filterDate, searchStudent]);

    const fetchRevenueData = async () => {
        try {
            const response = await axios.get('http://localhost:5000/manager/revenue', {
                params: { date: filterDate, student: searchStudent }
            });
            setRevenues(response.data);
            calculateSummary(response.data);
        } catch (error) {
            console.error('Lỗi khi tải dữ liệu doanh thu:', error);
        }
    };

    const calculateSummary = (data) => {
        const total = data.reduce((sum, item) => sum + item.already_pay, 0);
        const uniqueStudents = new Set(data.map(item => item.student_id));
        setTotalRevenue(total);
        setTotalStudents(uniqueStudents.size);
    };

    const exportToPDF = () => {
        const doc = new jsPDF();
        doc.text('Báo cáo doanh thu', 14, 10);

        autoTable(doc, {
            head: [['ID Học viên', 'Số tiền đã đóng', 'Ngày thanh toán']],
            body: revenues.map(item => [item.student_id, `${item.already_pay.toLocaleString('vi-VN')} VNĐ`, item.latest_pay_at])
        });

        doc.save('DoanhThu.pdf');
    };

    const exportToExcel = () => {
        const worksheet = XLSX.utils.json_to_sheet(revenues);
        const workbook = XLSX.utils.book_new();
        XLSX.utils.book_append_sheet(workbook, worksheet, 'Revenue');
        const excelBuffer = XLSX.write(workbook, { bookType: 'xlsx', type: 'array' });
        const data = new Blob([excelBuffer], { type: 'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet' });
        saveAs(data, 'DoanhThu.xlsx');
    };

    return (
        <div>
            <h2>Quản lý Doanh Thu</h2>
            <div>
                <label>Lọc theo ngày: </label>
                <input type="date" value={filterDate} onChange={e => setFilterDate(e.target.value)} />
                <label>Tìm học viên: </label>
                <input type="text" placeholder="ID hoặc tên" value={searchStudent} onChange={e => setSearchStudent(e.target.value)} />
            </div>
            <table>
                <thead>
                    <tr>
                        <th>ID Học viên</th>
                        <th>Số tiền đã đóng</th>
                        <th>Ngày thanh toán</th>
                    </tr>
                </thead>
                <tbody>
                    {revenues.map((item, index) => (
                        <tr key={index}>
                            <td>{item.student_id}</td>
                            <td>{item.already_pay.toLocaleString('vi-VN')} VNĐ</td>
                            <td>{item.latest_pay_at}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
            <div>
                <p><strong>Tổng doanh thu:</strong> {totalRevenue.toLocaleString('vi-VN')} VNĐ</p>
                <p><strong>Tổng số học viên đã đóng:</strong> {totalStudents}</p>
            </div>
            <button onClick={exportToPDF}>Xuất PDF</button>
            <button onClick={exportToExcel}>Xuất Excel</button>
        </div>
    );
};

export default TuitionSalary;
