import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "./Fees.css";

const Fees = ({ user }) => {
    const navigate = useNavigate();
    const [fees, setFees] = useState([]);

    useEffect(() => {
        if (!user) {
            const storedUser = JSON.parse(localStorage.getItem("user"));
            if (!storedUser) {
                navigate("/login");
                return;
            }
            fetchFees(storedUser.id);
        } else {
            fetchFees(user.id);
        }
    }, [user, navigate]);

    const fetchFees = async (studentId) => {
        try {
            const response = await fetch(`http://localhost:5000/student/fees/${studentId}`);
            if (!response.ok) throw new Error("L·ªói khi t·∫£i h·ªçc ph√≠");
            const data = await response.json();
            setFees(data);
        } catch (error) {
            console.error(error);
            setFees([]);
        }
    };

    return (
        <div className="fees-container">
            <h2>üí∏ H·ªçc ph√≠ c·ªßa b·∫°n</h2>
            {fees.length === 0 ? (
                <p>Ch∆∞a c√≥ d·ªØ li·ªáu h·ªçc ph√≠.</p>
            ) : (
                <table className="fees-table">
                    <thead>
                        <tr>
                            <th>üìö S·ªë nh√≥m ƒëƒÉng k√Ω</th> {/* üî• ƒê·ªïi "S·ªë m√¥n ƒëƒÉng k√Ω" ‚Üí "S·ªë nh√≥m ƒëƒÉng k√Ω" */}
                            <th>üí∞ T·ªïng h·ªçc ph√≠</th>
                            <th>üìÖ Ng√†y ƒë√≥ng g·∫ßn nh·∫•t</th>
                            <th>üí≥ ƒê√£ thanh to√°n</th>
                            <th>üí∏ C√≤n l·∫°i</th>
                        </tr>
                    </thead>
                    <tbody>
                        {fees.map((fee) => (
                            <tr key={fee.student_id}>  {/* üî• S·ª≠a `fee.id` ‚Üí `fee.student_id` */}
                                <td>{fee.group_count}</td>  {/* üî• ƒê·ªïi `subject_count` ‚Üí `group_count` */}
                                <td>{fee.total_fee ? fee.total_fee.toLocaleString() : "N/A"} VNƒê</td>
                                <td>{fee.latest_pay_at ? new Date(fee.latest_pay_at).toLocaleDateString() : "Ch∆∞a ƒë√≥ng"}</td>
                                <td>{(fee.already_pay || 0).toLocaleString()} VNƒê</td>
                                <td>{(fee.remaining || 0).toLocaleString()} VNƒê</td>
                            </tr>
                        ))}
                    </tbody>
                </table>

            )}
        </div>
    );
};

export default Fees;
