                <tbody>
                    <tr>
                        <td>{fees?.paid_groups || "-"}</td>
                        <td>{fees?.paid_group_count || 0}</td>
                        <td>{fees?.total_paid_fee ? fees.total_paid_fee.toLocaleString() : "0"} VNƒê</td>
                        <td>{fees?.last_payment_date ? new Date(fees.last_payment_date).toLocaleDateString() : "-"}</td>
                        <td style={{ color: "red" }}>{fees?.remaining_paid ? fees.remaining_paid.toLocaleString() : "0"} VNƒê</td>

