import { useState, useEffect, useRef } from "react";
import StudentClasses from "./student";
import TeacherClasses from "./teacher";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { toast } from "react-toastify"
import "react-toastify/dist/ReactToastify.css";
import "./Documents.css";

const Courses = ({ user }) => {
    const [selectedClass, setSelectedClass] = useState(null);
    const [announcements, setAnnouncements] = useState([]);
    const [assignments, setAssignments] = useState([]);
    const [submittedAssignments, setSubmittedAssignments] = useState([]);
    const [students, setStudents] = useState([]);
    const navigate = useNavigate();
    const [newAnnouncement, setNewAnnouncement] = useState("");
    const [selectedFile, setSelectedFile] = useState(null);
    const fileInputRef = useRef(null);
    const storedUser = localStorage.getItem("user");
    const [currentUser, setCurrentUser] = useState(user || (storedUser ? JSON.parse(storedUser) : null));

    useEffect(() => {
        if (!currentUser) {
            navigate("/login");
        }
    }, [currentUser, navigate]);

    const handleSelectClass = async (class_Id) => {
        setSelectedClass(class_Id);
        try {
            const [annRes, assignRes, studentRes, submitRes] = await Promise.all([
                axios.get(`http://localhost:5000/announcements/class/${class_Id}`),
                axios.get(`http://localhost:5000/assignments/class/${class_Id}`),
                axios.get(`http://localhost:5000/students/class/${class_Id}`),
                axios.get(`http://localhost:5000/submissions/class/${class_Id}`)
            ]);
            setAnnouncements(annRes.data);
            setAssignments(assignRes.data);
            setStudents(studentRes.data);
            setSubmittedAssignments(submitRes.data);
        } catch (error) {
            console.error("‚ùå L·ªói khi t·∫£i d·ªØ li·ªáu l·ªõp h·ªçc:", error);
            toast.error("‚ö†Ô∏è Kh√¥ng th·ªÉ t·∫£i d·ªØ li·ªáu l·ªõp h·ªçc!");
        }
    };

    const handlePostAnnouncement = async () => {
        if (!newAnnouncement.trim()) return;
        try {
            await axios.post("http://localhost:5000/announcements", {
                class_id: selectedClass,
                teacher_id: currentUser.id,
                content: newAnnouncement,
            });
            setNewAnnouncement("");
            handleSelectClass(selectedClass);
            toast.success("‚úÖ ƒê√£ ƒëƒÉng th√¥ng b√°o!");
        } catch (error) {
            console.error("‚ùå L·ªói khi ƒëƒÉng th√¥ng b√°o:", error);
            toast.error("‚ö†Ô∏è Kh√¥ng th·ªÉ ƒëƒÉng th√¥ng b√°o!");
        }
    };

    const handleUploadAssignment = async (event) => {
        const file = event.target.files[0];
        if (!file) return;

        const formData = new FormData();
        formData.append("file", file);
        formData.append("class_id", selectedClass);
        formData.append("student_id", currentUser.id);

        try {
            await axios.post("http://localhost:5000/assignments/upload", formData, {
                headers: { "Content-Type": "multipart/form-data" },
            });
            toast.success("‚úÖ ƒê√£ n·ªôp b√†i t·∫≠p!");
            handleSelectClass(selectedClass);
        } catch (error) {
            console.error("‚ùå L·ªói khi n·ªôp b√†i t·∫≠p:", error);
            toast.error("‚ö†Ô∏è Kh√¥ng th·ªÉ n·ªôp b√†i t·∫≠p!");
        }
    };

    return (
        <div className="courses-container">
            <h2>Kh√≥a h·ªçc c·ªßa b·∫°n</h2>

            {!selectedClass ? (
                <>
                    <h3>Ch·ªçn l·ªõp h·ªçc</h3>
                    {currentUser?.role === "hv" ? (
                        <StudentClasses userId={currentUser.id} onSelectClass={handleSelectClass} />
                    ) : currentUser?.role === "gv" ? (
                        <TeacherClasses userId={currentUser.id} onSelectClass={handleSelectClass} />
                    ) : (
                        <p>ƒêang t·∫£i d·ªØ li·ªáu ng∆∞·ªùi d√πng...</p>
                    )}
                </>
            ) : (
                <div className="course-content">
                    <button onClick={() => setSelectedClass(null)}>üîô Quay l·∫°i danh s√°ch l·ªõp</button>

                    {currentUser?.role === "gv" && (
                        <div className="announcement-section">
                            <h3>üì¢ ƒêƒÉng th√¥ng b√°o</h3>
                            <textarea
                                value={newAnnouncement}
                                onChange={(e) => setNewAnnouncement(e.target.value)}
                                placeholder="Nh·∫≠p n·ªôi dung th√¥ng b√°o..."
                            />
                            <button onClick={handlePostAnnouncement}>üì§ ƒêƒÉng</button>
                        </div>
                    )}

                    <h3>üì¢ Th√¥ng b√°o</h3>
                    <ul>
                        {announcements.map((ann) => (
                            <li key={ann.id}>{ann.content}</li>
                        ))}
                    </ul>

                    <h3>üìù B√†i t·∫≠p</h3>
                    {currentUser?.role === "hv" && (
                        <>
                            <input
                                type="file"
                                accept=".pdf,.docx,.pptx"
                                ref={fileInputRef}
                                style={{ display: "none" }}
                                onChange={handleUploadAssignment}
                            />
                            <button onClick={() => fileInputRef.current.click()}>üì§ N·ªôp b√†i t·∫≠p</button>
                        </>
                    )}
                    <ul>
                        {assignments.map((assign) => (
                            <li key={assign.id}>{assign.title}</li>
                        ))}
                    </ul>

                    {currentUser?.role === "gv" && (
                        <>
                            <h3>üì• B√†i t·∫≠p ƒë√£ n·ªôp</h3>
                            <ul>
                                {submittedAssignments.map((sub) => (
                                    <li key={sub.id}><a href={`http://localhost:5000${sub.file_path}`} target="_blank" rel="noopener noreferrer">{sub.student_name}</a></li>
                                ))}
                            </ul>

                            <h3>üë• Danh s√°ch h·ªçc vi√™n</h3>
                            <ul>
                                {students.map((student) => (
                                    <li key={student.id}>{student.name}</li>
                                ))}
                            </ul>
                        </>
                    )}
                </div>
            )}
        </div>
    );
};

export default Courses;
