import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const Notifications = () => {
    const [notifications, setNotifications] = useState([]);
    const [userRole, setUserRole] = useState(""); // Vai tr√≤ c·ªßa ng∆∞·ªùi d√πng
    const [newNotification, setNewNotification] = useState({ title: "", message: "", recipient: "users" });
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState("");

    const navigate = useNavigate();



    // G·ªçi API khi userRole ƒë∆∞·ª£c c·∫≠p nh·∫≠t
    useEffect(() => {
        if (userRole) {
            fetchNotifications(userRole);
        }
    }, [userRole]);
    const fetchUserRole = () => {
        const user = JSON.parse(localStorage.getItem("user"));
        if (user && user.role) {
            setUserRole(user.role);
        } else {
            setError("Kh√¥ng t√¨m th·∫•y vai tr√≤ ng∆∞·ªùi d√πng.");
            setLoading(false);
        }
    };

    const fetchNotifications = async (userRole) => {
        console.log("üîç ƒêang g·ªçi API v·ªõi role:", userRole);
        try {
            const response = await axios.get(`http://localhost:5000/notifications/notifications?role=${userRole}`);
            console.log("‚úÖ D·ªØ li·ªáu nh·∫≠n ƒë∆∞·ª£c:", response.data);
            setNotifications(response.data);
        } catch (error) {
            console.error("‚ùå L·ªói khi t·∫£i th√¥ng b√°o:", error.response?.data || error.message);
        } finally {
            setLoading(false); // ƒê·∫£m b·∫£o loading ƒë∆∞·ª£c t·∫Øt d√π c√≥ l·ªói hay kh√¥ng
        }
    };




    const handleDelete = async (id) => {
        if (userRole !== "ad" && userRole !== "cm") {
            alert("B·∫°n kh√¥ng c√≥ quy·ªÅn x√≥a th√¥ng b√°o.");
            return;
        }

        try {
            await axios.delete(`http://localhost:5000/notifications/notifications/${id}`, {
                headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
            });
            setNotifications(notifications.filter(notification => notification.id !== id));
        } catch (error) {
            setError("L·ªói khi x√≥a th√¥ng b√°o.");
        }
    };

    const handleCreate = async () => {
        if (userRole !== "ad" && userRole !== "cm") {
            alert("B·∫°n kh√¥ng c√≥ quy·ªÅn t·∫°o th√¥ng b√°o.");
            return;
        }
        if (!newNotification.title || !newNotification.message) {
            alert("Vui l√≤ng nh·∫≠p ti√™u ƒë·ªÅ v√† n·ªôi dung.");
            return;
        }

        try {
            await axios.post(
                "http://localhost:5000/notifications/notifications",
                newNotification,
                { headers: { Authorization: `Bearer ${localStorage.getItem("token")}` } }
            );
            setNewNotification({ title: "", message: "", recipient: "users" });
            fetchNotifications(userRole);
        } catch (error) {
            setError("L·ªói t·∫°o th√¥ng b√°o.");
        }
    };

    const filteredNotifications = notifications.filter(
        (notification) => notification.recipient === "users" || notification.recipient === userRole
    );

    return (
        <div style={{ maxWidth: "600px", margin: "auto", padding: "20px" }}>
            <h2>üì¢ Th√¥ng b√°o</h2>
            {notifications.length > 0 ? (
                <ul>
                    {notifications.map((noti) => (
                        <li key={noti.id}>
                            <strong>{noti.title}</strong>: {noti.message}
                        </li>
                    ))}
                </ul>
            ) : (
                <p>‚è≥ ƒêang t·∫£i th√¥ng b√°o...</p>
            )}

            {/* Form t·∫°o th√¥ng b√°o */}
            {(userRole === "ad" || userRole === "cm") && (
                <div style={{ marginBottom: "20px", padding: "10px", border: "1px solid #ccc", borderRadius: "5px" }}>
                    <h3>‚ûï Th√™m th√¥ng b√°o</h3>
                    <input
                        type="text"
                        placeholder="Ti√™u ƒë·ªÅ"
                        value={newNotification.title}
                        onChange={(e) => setNewNotification({ ...newNotification, title: e.target.value })}
                        style={{ width: "100%", marginBottom: "10px", padding: "8px" }}
                    />
                    <textarea
                        placeholder="N·ªôi dung"
                        value={newNotification.message}
                        onChange={(e) => setNewNotification({ ...newNotification, message: e.target.value })}
                        style={{ width: "100%", marginBottom: "10px", padding: "8px" }}
                    />
                    <select
                        value={newNotification.recipient}
                        onChange={(e) => setNewNotification({ ...newNotification, recipient: e.target.value })}
                        style={{ width: "100%", marginBottom: "10px", padding: "8px" }}
                    >
                        <option value="users">T·∫•t c·∫£ ng∆∞·ªùi d√πng</option>
                        <option value="hv">H·ªçc vi√™n</option>
                        <option value="gv">Gi√°o vi√™n</option>
                        <option value="cm">Qu·∫£n l√Ω</option>
                        <option value="ad">Admin</option>
                    </select>
                    <button onClick={handleCreate} style={{ padding: "10px", backgroundColor: "green", color: "white", border: "none", cursor: "pointer" }}>Th√™m</button>
                </div>
            )}

            {/* Danh s√°ch th√¥ng b√°o */}
            <ul style={{ listStyle: "none", padding: 0 }}>
                {filteredNotifications.map((notification) => (
                    <li key={notification.id} style={{ padding: "10px", borderBottom: "1px solid #ddd" }}>
                        <h4 style={{ margin: "5px 0" }}>{notification.title} <small>({notification.recipient})</small></h4>
                        <p style={{ margin: "5px 0" }}>{notification.message}</p>
                        {(userRole === "ad" || userRole === "cm") && (
                            <button
                                onClick={() => handleDelete(notification.id)}
                                style={{ padding: "5px 10px", backgroundColor: "red", color: "white", border: "none", cursor: "pointer" }}
                            >
                                X√≥a
                            </button>
                        )}
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default Notifications;
