// components/MakeUpClasses.js
import React, { useState, useEffect } from 'react';
import { useUser } from '../context/UserContext';  // Import UserContext

const MakeUpClasses = () => {
    const { user } = useUser();  // Lấy thông tin người dùng từ context
    const [makeUpClasses, setMakeUpClasses] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        if (user && user.studentId) {
            // Gọi API lấy lớp học bù với studentId từ context
            fetch(`http://localhost:5000/student/make-up-classes/${user.studentId}`)
                .then((response) => response.json())
                .then((data) => {
                    setMakeUpClasses(data);
                    setLoading(false);
                })
                .catch((error) => {
                    console.error('Lỗi lấy lớp học bù:', error);
                    setLoading(false);
                });
        }
    }, [user]);

    if (loading) {
        return <p>Đang tải dữ liệu...</p>;
    }

    return (
        <div>
            <h2>Danh sách lớp học bù</h2>
            {makeUpClasses.length === 0 ? (
                <p>Không có lớp học bù nào.</p>
            ) : (
                <ul>
                    {makeUpClasses.map((makeUpClass, index) => (
                        <li key={index}>
                            <p><strong>Lớp học:</strong> {makeUpClass.class_name}</p>
                            <p><strong>Ngày học bù:</strong> {new Date(makeUpClass.make_up_date).toLocaleDateString()}</p>
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
};

export default MakeUpClasses;
