import React, { useEffect, useState } from "react";
import axios from "axios";

const ManageGroups = () => {
    const [groups, setGroups] = useState([]);
    const [newGroup, setNewGroup] = useState("");

    useEffect(() => {
        fetchGroups();
    }, []);

    const fetchGroups = async () => {
        try {
            const res = await axios.get("http://localhost:5000/groups"); // N·∫øu backend kh√¥ng c√≥ /api th√¨ gi·ªØ nguy√™n
            setGroups(res.data);
        } catch (error) {
            console.error("L·ªói l·∫•y danh s√°ch nh√≥m h·ªçc:", error);
        }
    };

    const addGroup = async () => {
        if (!newGroup.trim()) return;
        try {
            await axios.post("http://localhost:5000/groups", { name: newGroup });
            setNewGroup("");
            await fetchGroups();
        } catch (error) {
            console.error("L·ªói th√™m nh√≥m h·ªçc:", error);
        }
    };

    const deleteGroup = async (id) => {
        try {
            await axios.delete(`http://localhost:5000/groups/${id}`); // B·ªè `/api/`
            await fetchGroups();
        } catch (error) {
            console.error("L·ªói x√≥a nh√≥m h·ªçc:", error);
        }
    };

    return (
        <div>
            <h2>üìñ Qu·∫£n l√Ω nh√≥m h·ªçc</h2>
            <input
                type="text"
                value={newGroup}
                onChange={(e) => setNewGroup(e.target.value)}
                placeholder="T√™n nh√≥m m·ªõi"
            />
            <button onClick={addGroup}>Th√™m nh√≥m</button>
            <ul>
                {groups.map((group) => (
                    <li key={group.id}>
                        {group.name}
                        <button onClick={() => deleteGroup(group.id)}>üóëÔ∏è X√≥a</button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default ManageGroups;
