import { useState } from "react";
import StudentClasses from "./student";
import TeacherClasses from "./teacher";
import { useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { io } from "socket.io-client";
import axios from "axios";
import { toast } from "react-toastify";
import { FaFileAlt, FaTrash, FaDownload } from "react-icons/fa";
import "react-toastify/dist/ReactToastify.css";
import "./Documents.css";
const socket = io("http://localhost:5000");

const Documents = ({ user }) => {
    const [selectedClass, setSelectedClass] = useState(null);
    const [documents, setDocuments] = useState([]);
    const navigate = useNavigate();
    const [currentUser, setCurrentUser] = useState(user || JSON.parse(localStorage.getItem("user")));
    const [notifications, setNotifications] = useState([]);

    useEffect(() => {
        const storedUser = JSON.parse(localStorage.getItem("user"));
        if (!storedUser) {
            navigate("/login");
        } else {
            setCurrentUser(storedUser);
        }

        socket.emit("join", { userId: storedUser.id });

        socket.on("newNotification", (notification) => {
            setNotifications((prev) => [notification, ...prev]);
        });

        return () => {
            socket.off("newNotification");
        };
    }, [navigate]);
    const handleSelectClass = async (classId) => {
        try {
            const response = await axios.get(`http://localhost:5000/documents/${classId}`);
            setDocuments(response.data);
            setSelectedClass(classId);
        } catch (error) {
            console.error("‚ùå L·ªói khi l·∫•y t√†i li·ªáu:", error);
        }
    };

    return (
        <div className="documents-container">
            <h2>T√†i li·ªáu l·ªõp h·ªçc</h2>

            {!selectedClass ? (
                <>
                    <h3>Ch·ªçn l·ªõp ƒë·ªÉ xem t√†i li·ªáu</h3>
                    {user.role === "hv" ? (
                        <StudentClasses userId={user.id} onSelectClass={handleSelectClass} />
                    ) : (
                        <TeacherClasses userId={user.id} onSelectClass={handleSelectClass} />
                    )}
                </>
            ) : (
                <div className="document-list">
                    <button onClick={() => setSelectedClass(null)}>üîô Quay l·∫°i danh s√°ch l·ªõp</button>
                    <h3>Danh s√°ch t√†i li·ªáu</h3>
                    <ul>
                        {documents.length === 0 ? (
                            <p>Kh√¥ng c√≥ t√†i li·ªáu n√†o.</p>
                        ) : (
                            documents.map((doc) => (
                                <li key={doc.id}>
                                    <a href={doc.url} target="_blank" rel="noopener noreferrer">
                                        {doc.name}
                                    </a>
                                </li>
                            ))
                        )}
                    </ul>
                </div>
            )}
        </div>
    );
};

export default Documents;
