import { useEffect, useState } from 'react';
import { Link, Outlet, useNavigate } from 'react-router-dom';
import './Home.css';
import PropTypes from 'prop-types';

function Home({ isLoggedIn }) {
    const navigate = useNavigate();
    const [userRole, setUserRole] = useState(null);
    const [notifications, setNotifications] = useState([]);
    //  const [settings, setSettings] = useState({ theme: 'light', language: 'vi' });

    // Lấy userRole và thông báo từ localStorage khi component được tải
    useEffect(() => {
        const user = localStorage.getItem('user');
        if (user) {
            const parsedUser = JSON.parse(user);
            setUserRole(parsedUser.role);

            // Lấy thông báo từ vai trò
            const roleNotifications = getNotificationsByRole(parsedUser.role);
            setNotifications(roleNotifications);
        }
    }, []);

    // Lấy thông báo theo vai trò
    const getNotificationsByRole = (role) => {
        switch (role) {
            case 'ad':
                return ['Hệ thống cần cập nhật định kỳ', 'Báo cáo tài chính đã sẵn sàng'];
            case 'cm':
                return ['Có nhóm mới cần sắp xếp giáo viên', 'Cập nhật lịch học tuần này'];
            case 'gv':
                return ['Bạn có buổi dạy vào chiều nay', 'Hãy nhập điểm cho lớp A2'];
            case 'hv':
                return ['Lịch học đã được cập nhật', 'Đã đến hạn đóng học phí tháng này'];
            default:
                return [];
        }
    };

    // Kiểm tra nếu người dùng chưa đăng nhập
    useEffect(() => {
        if (!isLoggedIn) {
            navigate('/login');
        }
    }, [isLoggedIn, navigate]);

    const handleLogout = () => {
        localStorage.removeItem('user');
        navigate('/login');
    };

    // const handleSettingsChange = (key, value) => {
    //   setSettings((prevSettings) => ({ ...prevSettings, [key]: value }));
    //};

    if (!isLoggedIn || userRole === null) {
        return <div>Đang tải...</div>;
    }

    return (
        <div className="home">
            {/* Navbar */}
            <header className="navbar">
                <div className="logo">Trung Tâm Dạy Thêm</div>
                <ul className="navbar-links">
                    <li><Link to="/home/personal-info">Thông Tin Cá Nhân</Link></li>
                    <li><button onClick={() => alert('Thông Báo: ' + notifications.join(', '))}>Thông Báo</button></li>
                    <li>
                        <button onClick={() => alert(`Cài Đặt:\nTheme:\nNgôn ngữ: `)}>
                            Cài Đặt
                        </button>
                    </li>
                    <li><button onClick={handleLogout}>Đăng Xuất</button></li>
                </ul>
            </header>

            {/* Body: Sidebar + Main Content */}
            <div className="home-body">
                {/* Sidebar */}
                <aside className="sidebar">
                    <div className="function-title">Chức năng</div>
                    <ul>
                        {userRole === 'ad' && (
                            <>
                                <li><Link to="/home/student-grouping">Phân nhóm học sinh theo điểm</Link></li>
                                <li><Link to="/home/attendance-report">Báo cáo chấm công</Link></li>
                                <li><Link to="/home/attendance-summary">Báo cáo chuyên cần</Link></li>
                                <li><Link to="/home/teacher-salary">Phiếu lương giáo viên</Link></li>
                                <li><Link to="/home/student-payment-confirmation">Phiếu xác nhận đóng tiền học sinh</Link></li>
                            </>
                        )}
                        {userRole === 'cm' && (
                            <>
                                <li><Link to="/home/group-management">Tạo nhóm học</Link></li>
                                <li><Link to="/home/schedule">Tạo thời khóa biểu</Link></li>
                                <li><Link to="/home/attendance">Điểm danh học sinh</Link></li>
                            </>
                        )}
                        {userRole === 'gv' && (
                            <>
                                <li><Link to="/home/attendance">Chấm công</Link></li>
                                <li><Link to="/home/grades">Nhập điểm học sinh</Link></li>
                                <li><Link to="/home/schedule">Xem lịch dạy</Link></li>
                                <li><Link to="/home/history">Xem ca học đã dạy</Link></li>
                            </>
                        )}
                        {userRole === 'hv' && (
                            <>
                                <li><Link to="/home/studentschedule/hv">Thời khóa biểu</Link></li>
                                <li><Link to="/home/grades/hv">Xem điểm</Link></li>
                                <li><Link to="/home/registerclass/hv">Đăng ký buổi học</Link></li>
                                <li><Link to="/home/paymenthistory/hv">Đóng tiền học</Link></li>
                                <li><Link to="/home/attendance/hv">Xem điểm danh</Link></li>
                            </>
                        )}

                    </ul>
                </aside>

                {/* Main Content */}
                <main className="main-content">
                    <Outlet />
                    {!window.location.pathname.includes('/home/') && (
                        <div className="dashboard">
                            {userRole === 'ad' && (
                                <>
                                    <h2>Bảng Điều Khiển Quản Trị</h2>
                                    <div className="dashboard-grid">
                                        <div className="card">
                                            <h3>👨‍🎓 Tổng số học viên</h3>
                                            <p>120 học viên</p>
                                        </div>
                                        <div className="card">
                                            <h3>👨‍🏫 Tổng số giáo viên</h3>
                                            <p>20 giáo viên</p>
                                        </div>
                                        <div className="card">
                                            <h3>💰 Doanh thu tháng này</h3>
                                            <p>50,000,000 VND</p>
                                        </div>
                                        <div className="card">
                                            <h3>📅 Số lớp đang hoạt động</h3>
                                            <p>10 lớp</p>
                                        </div>
                                    </div>
                                </>
                            )}
                            {userRole === 'cm' && (
                                <>
                                    <h2>Tổng Quan Trung Tâm</h2>
                                    <div className="dashboard-grid">
                                        <div className="card">
                                            <h3>🏫 Tổng số lớp học</h3>
                                            <p>10 lớp</p>
                                        </div>
                                        <div className="card">
                                            <h3>📅 Lịch dạy tuần này</h3>
                                            <p>50 buổi</p>
                                        </div>
                                        <div className="card">
                                            <h3>📊 Tình trạng lớp học</h3>
                                            <p>8 lớp hoạt động, 2 lớp nghỉ</p>
                                        </div>
                                        <div className="card">
                                            <h3>👨‍🏫 Giáo viên sắp xếp</h3>
                                            <p>20 giáo viên đã phân công</p>
                                        </div>
                                    </div>
                                </>
                            )}
                            {userRole === 'gv' && (
                                <>
                                    <h2>Thông Tin Giảng Dạy</h2>
                                    <div className="dashboard-grid">
                                        <div className="card">
                                            <h3>📅 Buổi dạy sắp tới</h3>
                                            <p>3 buổi</p>
                                        </div>
                                        <div className="card">
                                            <h3>📚 Lớp phụ trách</h3>
                                            <p>Toán A2, Lý B1</p>
                                        </div>
                                        <div className="card">
                                            <h3>📖 Số bài đã chấm</h3>
                                            <p>15 bài</p>
                                        </div>
                                        <div className="card">
                                            <h3>⏳ Thời gian làm việc</h3>
                                            <p>20 giờ dạy</p>
                                        </div>
                                    </div>
                                </>
                            )}
                            {userRole === 'hv' && (
                                <>
                                    <h2>Thông Tin Học Tập</h2>
                                    <div className="dashboard-grid">
                                        <div className="card">
                                            <h3>📅 Buổi học kế tiếp</h3>
                                            <p>Toán - 14/02/2025</p>
                                        </div>
                                        <div className="card">
                                            <h3>💵 Học phí cần đóng</h3>
                                            <p>2,000,000 VND</p>
                                        </div>
                                        <div className="card">
                                            <h3>📊 Điểm trung bình</h3>
                                            <p>8.5</p>
                                        </div>
                                        <div className="card">
                                            <h3>✅ Số buổi đã tham gia</h3>
                                            <p>18/20 buổi</p>
                                        </div>
                                    </div>
                                </>
                            )}
                        </div>
                    )}
                </main>

            </div>

            {/* Footer */}
            <footer className="footer">
                <p>&copy; 2025 Trung Tâm Dạy Thêm | Liên hệ: 123-456-789</p>
            </footer>
        </div>
    );
}

// Định nghĩa propTypes cho Home component
Home.propTypes = {
    isLoggedIn: PropTypes.bool.isRequired,
};

export default Home;
