import { useEffect, useState } from 'react';


function Attendance() {
    const [attendanceList, setAttendanceList] = useState([]);
    const [userRole, setUserRole] = useState(null);


    // Lấy userRole từ localStorage
    useEffect(() => {
        const user = localStorage.getItem('user');
        if (user) {
            const parsedUser = JSON.parse(user);
            setUserRole(parsedUser.role); // Lưu trữ userRole
        }
    }, []);

    // Giả lập lấy điểm danh từ server
    useEffect(() => {
        const mockAttendanceData = userRole === 'cm'
            ? [ // CM có thể xem điểm danh của tất cả học viên
                { name: 'Nguyễn Văn A', date: '2025-01-15', status: 'Đã điểm danh' },
                { name: 'Trần Thị B', date: '2025-01-16', status: 'Chưa điểm danh' }
            ]
            : [ // GV chỉ có thể xem điểm danh của học viên trong lớp của mình
                { name: 'Nguyễn Văn A', date: '2025-01-15', status: 'Đã điểm danh' },
            ];

        setAttendanceList(mockAttendanceData);
    }, [userRole]);

    if (!userRole) return <div>Đang tải...</div>;

    return (
        <div className="attendance">
            <h2>Điểm danh</h2>
            <table>
                <thead>
                    <tr>
                        <th>Tên học viên</th>
                        <th>Ngày</th>
                        <th>Trạng thái</th>
                    </tr>
                </thead>
                <tbody>
                    {attendanceList.map((item, index) => (
                        <tr key={index}>
                            <td>{item.name}</td>
                            <td>{item.date}</td>
                            <td>{item.status}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
}

export default Attendance;
