                <select
                    value={newGroup.max_student}
                    onChange={(e) => setNewGroup({ ...newGroup, max_student: e.target.value })}
                    className="w-full p-2 border rounded mb-2"
                >
                    <option value="">Ch·ªçn s·ªë h·ªçc sinh t·ªëi ƒëa</option>
                    {maxStudents
                        .filter((num) => num !== null)
