import { useEffect, useState } from "react";
import axios from "axios";

const FinanceReports = () => {
    const [report, setReport] = useState({ totalFees: 0, totalSalary: 0, profit: 0 });
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchReport = async () => {
            try {
                const response = await axios.get("http://localhost:5000/manager/finance");
                setReport(response.data);
            } catch (err) {
                console.error("L·ªói l·∫•y d·ªØ li·ªáu t√†i ch√≠nh:", err);
                setError("Kh√¥ng th·ªÉ t·∫£i d·ªØ li·ªáu t√†i ch√≠nh.");
            } finally {
                setLoading(false);
            }
        };
        fetchReport();
    }, []);

    if (loading) return <p className="text-center text-gray-600">ƒêang t·∫£i d·ªØ li·ªáu...</p>;
    if (error) return <p className="text-center text-red-500">{error}</p>;

    return (
        <div className="max-w-3xl mx-auto p-6 bg-white shadow-lg rounded-xl">
            <h2 className="text-2xl font-bold text-center text-gray-800 mb-4">üìä B√°o c√°o t√†i ch√≠nh</h2>
            <div className="grid grid-cols-3 gap-4">
                <div className="p-4 bg-blue-100 border-l-4 border-blue-500 rounded-lg shadow-sm">
                    <p className="text-lg font-semibold text-blue-700">T·ªïng h·ªçc ph√≠ thu</p>
                    <p className="text-xl font-bold text-blue-900">{report.totalFees.toLocaleString()} VND</p>
                </div>
                <div className="p-4 bg-red-100 border-l-4 border-red-500 rounded-lg shadow-sm">
                    <p className="text-lg font-semibold text-red-700">T·ªïng l∆∞∆°ng gi√°o vi√™n</p>
                    <p className="text-xl font-bold text-red-900">{report.totalSalary.toLocaleString()} VND</p>
                </div>
                <div className={`p-4 border-l-4 rounded-lg shadow-sm ${report.profit >= 0 ? "bg-green-100 border-green-500" : "bg-yellow-100 border-yellow-500"}`}>
                    <p className="text-lg font-semibold text-gray-700">L·ª£i nhu·∫≠n</p>
                    <p className={`text-xl font-bold ${report.profit >= 0 ? "text-green-900" : "text-yellow-900"}`}>
                        {report.profit.toLocaleString()} VND
                    </p>
                </div>
            </div>
        </div>
    );
};

export default FinanceReports;
