import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { FaHome, FaBell, FaCog, FaUser, FaSignOutAlt, FaChartBar, FaCalendarAlt, FaDatabase, FaEnvelope } from "react-icons/fa";
import "./Home.css";
import RevenueChart from "./Revenue";
import axios from "axios";
import Chart from "react-apexcharts";


const Home = ({ user }) => {
    const navigate = useNavigate();
    const [currentUser, setCurrentUser] = useState(user || JSON.parse(localStorage.getItem("user")));
    const [showSettings, setShowSettings] = useState(false);
    const [notifications, setNotifications] = useState([]);

    const [showNotifications, setShowNotifications] = useState(false);


    useEffect(() => {
        const storedUser = JSON.parse(localStorage.getItem("user"));
        if (!storedUser) {
            navigate("/login");
        } else {
            setCurrentUser(storedUser);
        }
        const fetchNotifications = async () => {
            try {
                const res = await axios.get("http://localhost:5000/admin/notifications", {
                    headers: { Authorization: `Bearer ${token}` },
                });
                setNotifications(res.data);
            } catch (error) {
                console.error("Lỗi lấy thông báo:", error);
            }
        };

        fetchNotifications();
    }, [navigate]);

    const handleLogout = () => {
        localStorage.removeItem("user");
        setCurrentUser(null);
        navigate("/login");
    };

    if (!currentUser) return <p>Đang tải...</p>;


    // Dữ liệu demo cho thống kê và biểu đồ
    const stats = {
        hv: { label: "Buổi học đã tham gia", value: 12 },
        gv: { label: "Lớp đang giảng dạy", value: 5 },
        cm: { label: "Doanh thu tháng này", value: "150,000,000 VNĐ" },
        ad: { label: "Tài khoản hệ thống", value: 120 },
    };


    const sidebarItems = {
        hv: [
            { path: "/schedule", label: "Lịch học", icon: <FaHome /> },
            { path: "/grades", label: "Điểm số", icon: <FaBell /> },
            { path: "/fees", label: "Học phí", icon: <FaCog /> },
            { path: "/makeup-classes", label: "Học bù", icon: <FaUser /> },
        ],
        gv: [
            { path: "/teaching-schedule", label: "Lịch giảng dạy", icon: <FaHome /> },
            { path: "/student-grades-input", label: "Chấm điểm học sinh", icon: <FaBell /> },
            { path: "/attendance", label: "Chấm công", icon: <FaCog /> },
        ],
        cm: [
            { path: "/manage-groups", label: "Quản lý nhóm học", icon: <FaHome /> },
            { path: "/finance-reports", label: "Báo cáo tài chính", icon: <FaBell /> },
            { path: "/manage-teachers", label: "Quản lý giáo viên", icon: <FaCog /> },
            { path: "/manage-students", label: "Quản lý học viên", icon: <FaUser /> },
            { path: "/manage-schedules", label: "Quản lý lịch học", icon: <FaHome /> },
            { path: "/tuition-salary", label: "Quản lý doanh thu", icon: <FaBell /> },
            { path: "/reports-statistics", label: "Báo cáo & Thống kê", icon: <FaCog /> },
        ],
        ad: [
            { path: "/system-management", label: "Quản lý hệ thống", icon: <FaHome /> },
            { path: "/revenue-salary", label: "Doanh thu & lương", icon: <FaBell /> },
            { path: "/attendance-reports", label: "Báo cáo chuyên cần", icon: <FaCog /> },
            { path: "/user-management", label: "Quản lý người dùng", icon: <FaUser /> },
            { path: "/role-management", label: "Phân quyền hệ thống", icon: <FaCog /> },
            { path: "/backup-restore", label: "Sao lưu & Khôi phục", icon: <FaDatabase /> }, // Mới thêm
            { path: "/send-notification", label: "Gửi thông báo", icon: <FaEnvelope /> }, // Mới thêm
            { path: "/finance-reports", label: "Báo cáo tài chính", icon: <FaChartBar /> }, // Mới thêm
        ],

    };

    return (
        <div className="home-container">
            <nav className="navbar">
                <h1>Trung Tâm Gia Sư</h1>
                <div className="navbar-buttons">
                    <span className="nav-item" onClick={() => navigate("/home")}><FaHome /> Trang chủ</span>
                    <span className="nav-item" onClick={() => navigate("/notifications")}><FaBell /> Thông báo</span>

                    {/* Cài đặt có menu dropdown */}
                    <div className="settings-container">
                        <span className="nav-item" onClick={() => setShowSettings(!showSettings)}>
                            <FaCog /> Cài đặt
                        </span>
                        {showSettings && (
                            <div className="settings-menu">
                                <span onClick={() => navigate("/settings/profile")}>🔹 Thông tin cá nhân</span>
                                <span onClick={() => navigate("/settings/password")}>🔹 Đổi mật khẩu</span>
                                <span onClick={() => navigate("/settings/preferences")}>🔹 Tùy chỉnh giao diện</span>
                            </div>
                        )}
                    </div>

                    <span className="nav-item" onClick={() => navigate("/profile")}><FaUser /> Thông tin cá nhân</span>
                    <span className="nav-item logout" onClick={handleLogout}><FaSignOutAlt /> Đăng xuất</span>
                </div>
            </nav>

            <div className="main-content">
                <aside className="sidebar">
                    <ul>
                        <li className="sidebar-item" onClick={() => navigate("/home")}><FaHome /> Trang chủ</li>
                        {sidebarItems[currentUser.role]?.map((item, index) => (
                            <li key={index} className="sidebar-item" onClick={() => navigate(item.path)}>
                                {item.icon} {item.label}
                            </li>
                        ))}
                    </ul>
                </aside>

                <main className="dashboard">
                    <section className="dashboard-content">
                        <h2>Thống kê doanh thu</h2>
                        <div className="chart-container">
                            <RevenueChart />
                        </div>
                        <p className="revenue-label">📌 {stats[currentUser.role].label}: {stats[currentUser.role].value}</p>

                    </section>
                </main>
            </div>
        </div>
    );
};

export default Home;
