import React, { useState } from "react";
import { QRCodeCanvas } from "qrcode.react";

const Payments = ({ user }) => {
    const [showQR, setShowQR] = useState(false);
    const [qrImage, setQrImage] = useState("");

    const generateQRValue = () => {
        const totalAmount = 100000; // Tổng số tiền học phí
        return `BIDV|CTUB${user.id}|HK2, 2024-2025, MSSV${user.id}, So tien ${totalAmount} VNĐ`;
    };

    const handleGenerateQR = () => {
        const qrCodeCanvas = document.getElementById("qrCodeCanvas");
        if (qrCodeCanvas) {
            const qrImage = qrCodeCanvas.toDataURL(); // Lấy ảnh QR code dạng base64
            setQrImage(qrImage);
        }
    };

    const handleUploadQR = async () => {
        try {
            const response = await fetch("http://localhost:5000/upload-qr", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify({ qrImage: qrImage }),
            });
            if (response.ok) {
                alert("QR code đã được tải lên thành công!");
            } else {
                alert("Có lỗi xảy ra khi tải lên!");
            }
        } catch (error) {
            console.error("Lỗi khi tải lên QR code", error);
            alert("Có lỗi xảy ra khi tải lên!");
        }
    };

    return (
        <div>
            <button onClick={() => setShowQR(!showQR)}>
                {showQR ? "Ẩn QR Code" : "Hiển thị QR Code"}
            </button>
            {showQR && (
                <div>
                    <QRCodeCanvas id="qrCodeCanvas" value={generateQRValue()} size={200} />
                    <button onClick={handleGenerateQR}>Tạo ảnh QR</button>
                    {qrImage && (
                        <div>
                            <img src={qrImage} alt="QR Code" />
                            <button onClick={handleUploadQR}>Tải lên QR Code</button>
                        </div>
                    )}
                </div>
            )}
        </div>
    );
};

export default Payments;
