import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "./Grades.css";

const Grades = ({ user }) => {
    const navigate = useNavigate();
    const [grades, setGrades] = useState([]);
    const [loading, setLoading] = useState(true);
    const [classList, setClassList] = useState([]);
    const [selectedClassId, setSelectedClassId] = useState("");

    useEffect(() => {
        if (!user) {
            const storedUser = JSON.parse(localStorage.getItem("user"));
            if (!storedUser) {
                navigate("/login");
                return;
            }
            fetchClassList(storedUser.id);
        } else {
            fetchClassList(user.id);
        }
    }, [user, navigate]);

    useEffect(() => {
        if (selectedClassId) fetchGrades(user?.id, selectedClassId);
    }, [selectedClassId]);

    const fetchClassList = async (userId) => {
        try {
            const response = await fetch(`http://localhost:5000/student/classes/${userId}`);
            if (!response.ok) throw new Error(`Lỗi tải danh sách lớp: ${response.status}`);
            const data = await response.json();
            setClassList(data);
            if (data.length > 0) setSelectedClassId(data[0].id); // Chọn lớp đầu tiên mặc định
        } catch (error) {
            console.error("❌ Lỗi:", error);
        }
    };

    const fetchGrades = async (userId, classId) => {
        setLoading(true);
        try {
            const response = await fetch(`http://localhost:5000/student/grades/${userId}/${classId}`);
            if (!response.ok) throw new Error(`Lỗi khi tải điểm số: ${response.status}`);
            const data = await response.json();
            setGrades(data);
        } catch (error) {
            console.error("❌ Lỗi:", error);
            setGrades([]);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="grades-container">
            <h2>📊 Điểm số của bạn</h2>

            {/* Dropdown chọn lớp học */}
            <div className="class-selector">
                <label htmlFor="classSelect">🏫 Chọn nhóm lớp:</label>
                <select
                    id="classSelect"
                    value={selectedClassId}
                    onChange={(e) => setSelectedClassId(e.target.value)}
                >
                    {classList.map((classItem) => (
                        <option key={classItem.id} value={classItem.id}>
                            {classItem.name} - {classItem.subject}
                        </option>
                    ))}
                </select>
            </div>

            {loading ? (
                <p className="loading">🔄 Đang tải...</p>
            ) : grades.length === 0 ? (
                <p>❌ Chưa có điểm cho lớp này.</p>
            ) : (
                <table className="grades-table">
                    <thead>
                        <tr>
                            <th>📝 Bài kiểm tra</th>
                            <th>📊 Điểm số</th>
                            <th>📅 Ngày thi</th>
                        </tr>
                    </thead>
                    <tbody>
                        {grades.map((grade, index) => (
                            <tr key={index}>
                                <td>{grade.exam_name}</td>
                                <td>{grade.score}</td>
                                <td>{new Date(grade.exam_date).toLocaleDateString()}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            )}
        </div>
    );
};

export default Grades;
