import { useEffect, useState } from 'react';
import axios from 'axios';

const TeacherSchedule = () => {
    const [schedule, setSchedule] = useState([]);
    const [teacherId, setTeacherId] = useState(null);

    const fetchTeacherSchedule = async () => {
        try {
            const response = await axios.get(`http://localhost:5000/teacher/classes/${teacherId}`);
            setSchedule(response.data);
        } catch (error) {
            console.error("Error fetching teacher schedule", error);
        }
    };


    useEffect(() => {
        const user = JSON.parse(localStorage.getItem("user"));
        if (user && user.id) {
            setTeacherId(user.id);  // Set teacher ID from local storage
        }
    }, []);

    useEffect(() => {
        if (teacherId) {
            fetchTeacherSchedule();  // Fetch schedule when teacherId is available
        }
    }, [teacherId]);

    // Function to format the date in "DD-MM-YYYY" format
    const formatDate = (dateString) => {
        const date = new Date(dateString);
        const day = String(date.getDate()).padStart(2, '0');
        const month = String(date.getMonth() + 1).padStart(2, '0');
        const year = date.getFullYear();
        return `${day}-${month}-${year}`;
    };

    return (
        <div className="schedule-container">
            <h2 className="schedule-title">üìÖ L·ªãch gi·∫£ng d·∫°y c·ªßa b·∫°n</h2>

            {schedule.length === 0 ? (
                <p className="no-schedule">Kh√¥ng c√≥ l·ªãch gi·∫£ng d·∫°y n√†o.</p>
            ) : (
                <table className="schedule-table">
                    <thead>
                        <tr>
                            <th>üìÖ Ng√†y h·ªçc</th>
                            <th>üìö T√™n nh√≥m</th>
                            <th>üìÜ Ng√†y trong tu·∫ßn</th>
                            <th>‚è∞ Gi·ªù b·∫Øt ƒë·∫ßu</th>
                            <th>‚è∞ Gi·ªù k·∫øt th√∫c</th>
                            <th>üè´ Ph√≤ng h·ªçc</th>

                        </tr>
                    </thead>
                    <tbody>
                        {schedule.map((item, index) => (
                            <tr key={index}>
                                <td>{formatDate(item.schedule_date)}</td> {/* Format the date here */}
                                <td>{item.group_name}</td>
