// components/MakeUpClasses.js

import React, { useState, useEffect } from 'react';

const MakeUpClasses = ({ studentId }) => {
    const [makeUpClasses, setMakeUpClasses] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        // Gọi API để lấy lớp học bù
        fetch(`http://localhost:5000/student/make-up-classes/${studentId}`)
            .then(response => response.json())
            .then(data => {
                setMakeUpClasses(data);  // Lưu dữ liệu vào state
                setLoading(false);  // Đổi trạng thái loading khi đã có dữ liệu
            })
            .catch(error => {
                console.error("Error fetching make-up classes:", error);
                setLoading(false);
            });
    }, [studentId]);  // Chạy lại khi `studentId` thay đổi

    if (loading) {
        return <p>Đang tải dữ liệu...</p>;
    }

    return (
        <div>
            <h2>Danh sách lớp học bù</h2>
            {makeUpClasses.length === 0 ? (
                <p>Không có lớp học bù nào.</p>
            ) : (
                <ul>
                    {makeUpClasses.map((makeUpClass, index) => (
                        <li key={index}>
                            <p><strong>Lớp học:</strong> {makeUpClass.class_name}</p>
                            <p><strong>Ngày học bù:</strong> {new Date(makeUpClass.make_up_date).toLocaleDateString()}</p>
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
};

export default MakeUpClasses;
