import { Rnd } from "react-rnd";
import "./DraggablePopup.css"; // Import CSS

const DraggablePopup = ({ children }) => {
    return (
        <Rnd
            default={{
                x: window.innerWidth / 2 - 200,
                y: window.innerHeight / 2 - 150,
                width: 400,
                height: "auto",
            }}
            enableResizing={false}
            dragHandleClassName="popup-header"
            className="draggable-popup" // Thêm class để CSS có hiệu lực
            style={{
                backgroundColor: "white",
                borderRadius: "10px",
                padding: "15px",
                boxShadow: "0 4px 8px rgba(0, 0, 0, 0.2)", // Thêm hiệu ứng bóng cho popup
            }}
        >
            {children}
        </Rnd>
    );
};

export default DraggablePopup;
