import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { io } from "socket.io-client";
import axios from "axios";
import "./Home.css";
import { FaHome, FaBell, FaSignOutAlt, FaBookOpen } from "react-icons/fa";

const socket = io("http://localhost:5000");

const Home = ({ user, token }) => {
    const navigate = useNavigate();
    const [currentUser, setCurrentUser] = useState(user || JSON.parse(localStorage.getItem("user")));
    const [groups, setGroups] = useState([]);
    const [registeredGroups, setRegisteredGroups] = useState([]);
    const [activeTab, setActiveTab] = useState("home"); // "home" ho·∫∑c "register"

    useEffect(() => {
        const storedUser = JSON.parse(localStorage.getItem("user"));
        if (!storedUser) {
            navigate("/login");
        } else {
            setCurrentUser(storedUser);
            fetchGroups();
            fetchRegisteredGroups(storedUser.id);
        }
    }, [navigate]);

    const fetchGroups = async () => {
        try {
            const res = await axios.get("http://localhost:5000/api/groups");
            setGroups(res.data);
        } catch (err) {
            console.error("L·ªói t·∫£i nh√≥m h·ªçc:", err);
        }
    };

    const fetchRegisteredGroups = async (userId) => {
        try {
            const res = await axios.get(`http://localhost:5000/api/registered-groups/${userId}`);
            setRegisteredGroups(res.data);
        } catch (err) {
            console.error("L·ªói t·∫£i nh√≥m ƒë√£ ƒëƒÉng k√Ω:", err);
        }
    };

    const handleRegister = async (groupId) => {
        try {
            const response = await axios.post(
                "http://localhost:5000/api/register-group",
                { userId: currentUser.id, groupId },
                { headers: { Authorization: `Bearer ${token}` } }
            );
            alert(response.data.message);
            fetchRegisteredGroups(currentUser.id);
        } catch (error) {
            console.error("L·ªói ƒëƒÉng k√Ω nh√≥m:", error);
            alert("ƒêƒÉng k√Ω th·∫•t b·∫°i!");
        }
    };

    return (
        <div className="home-container">
            <nav className="navbar">
                <h1>Trung T√¢m Gia S∆∞</h1>
                <div className="navbar-buttons">
                    <span className="nav-item" onClick={() => setActiveTab("home")} title="Trang ch·ªß">
                        <FaHome />
                    </span>
                    <span className="nav-item" onClick={() => navigate("/notifications")} title="Th√¥ng b√°o">
                        <FaBell />
                    </span>
                    <span className="nav-item logout" onClick={() => {
                        localStorage.removeItem("user");
                        navigate("/login");
                    }} title="ƒêƒÉng xu·∫•t">
                        <FaSignOutAlt />
                    </span>
                </div>
            </nav>

            <div className="main-content">
                <aside className="sidebar">
                    <ul>
                        <li onClick={() => setActiveTab("home")}>üè† Trang ch·ªß</li>
                        <li onClick={() => setActiveTab("register")}>üìö ƒêƒÉng k√Ω nh√≥m h·ªçc</li>
                    </ul>
                </aside>

                <main className="dashboard">
                    {activeTab === "home" ? (
                        <h2>Ch√†o m·ª´ng, {currentUser.username}!</h2>
                    ) : (
                        <>
                            <h2>ƒêƒÉng k√Ω nh√≥m h·ªçc</h2>
                            <h3>Nh√≥m h·ªçc c√≥ s·∫µn</h3>
                            <table className="group-table">
                                <thead>
                                    <tr>
                                        <th>T√™n nh√≥m</th>
                                        <th>Gi√°o vi√™n</th>
                                        <th>L·ªãch h·ªçc</th>
                                        <th>ƒêƒÉng k√Ω</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {groups.map((group) => (
                                        <tr key={group.id}>
                                            <td>{group.name}</td>
                                            <td>{group.teacher}</td>
                                            <td>{group.schedule}</td>
                                            <td>
                                                <button onClick={() => handleRegister(group.id)}>ƒêƒÉng k√Ω</button>
                                            </td>
                                        </tr>
                                    ))}
                                </tbody>
                            </table>

                            <h3>Nh√≥m h·ªçc ƒë√£ ƒëƒÉng k√Ω</h3>
                            <table className="group-table">
                                <thead>
                                    <tr>
                                        <th>T√™n nh√≥m</th>
                                        <th>Gi√°o vi√™n</th>
                                        <th>L·ªãch h·ªçc</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {registeredGroups.map((group) => (
                                        <tr key={group.id}>
                                            <td>{group.name}</td>
                                            <td>{group.teacher}</td>
                                            <td>{group.schedule}</td>
                                        </tr>
                                    ))}
                                </tbody>
                            </table>
                        </>
                    )}
                </main>
            </div>
        </div>
    );
};

export default Home;
