import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "./Fees.css";

const Payments = () => {
    const navigate = useNavigate();
    const [fees, setFees] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [paymentMethod, setPaymentMethod] = useState("bank");
    const [showQR, setShowQR] = useState(false);
    const [user, setUser] = useState(null);

    useEffect(() => {
        const storedUser = JSON.parse(localStorage.getItem("user"));
        if (!storedUser) {
            navigate("/login");
        } else {
            setUser(storedUser);
            fetchFees(storedUser.id);
        }
    }, [navigate]);

    const fetchFees = async (studentId) => {
        setIsLoading(true);
        try {
            const response = await fetch(`http://localhost:5000/fees/student/${studentId}`);
            if (!response.ok) throw new Error("Failed to fetch fees");
            const data = await response.json();
            setFees(data);
        } catch (error) {
            console.error("Error fetching fees:", error);
            setFees([]);
        }
        setIsLoading(false);
    };

    const handlePayment = async () => {
        const unpaidFees = fees.filter(fee => fee.remaining > 0);
        if (unpaidFees.length === 0) {
            alert("❌ No pending payments.");
            return;
        }

        const paymentData = {
            studentId: user?.id,
            amount: unpaidFees.reduce((sum, fee) => sum + fee.remaining, 0)
        };

        const res = await fetch("http://localhost:5000/fees/pay", {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify(paymentData)
        });

        const result = await res.json();
        if (result.success) {
            fetchFees(user.id);
        }
    };

    if (isLoading) {
        return <p>🔄 Loading fees data...</p>;
    }

    return (
        <div className="fees-container">
            <h2>💳 Tuition Fee Payment</h2>

            {fees.length === 0 ? (
                <p>No fee data available.</p>
            ) : (
                <>
                    <table className="fees-table">
                        <thead>
                            <tr>
                                <th>📚 Course</th>
                                <th>💰 Total Fee</th>
                                <th>💳 Paid</th>
                                <th>💸 Remaining</th>
                            </tr>
                        </thead>
                        <tbody>
                            {fees.map((fee) => (
                                <tr key={fee.group_id} style={{ backgroundColor: fee.remaining > 0 ? "#ffebee" : "#e8f5e9" }}>
                                    <td>{fee.group_id}</td>
                                    <td>{fee.total_fee?.toLocaleString()} VNĐ</td>
                                    <td>{(fee.already_pay || 0).toLocaleString()} VNĐ</td>
                                    <td style={{ fontWeight: "bold", color: fee.remaining > 0 ? "red" : "green" }}>
                                        {(fee.remaining || 0).toLocaleString()} VNĐ
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>

                    {fees.some(fee => fee.remaining > 0) && (
                        <div className="payment-section">
                            <h3>🔹 Payment Section</h3>
                            <p>Total amount due: <strong>{fees.reduce((sum, fee) => sum + (fee.remaining || 0), 0).toLocaleString()} VNĐ</strong></p>

                            <label>Select payment method:</label>
                            <select value={paymentMethod} onChange={(e) => setPaymentMethod(e.target.value)}>
                                <option value="bank">💳 Bank Transfer</option>
                                <option value="cash">💵 Cash</option>
                            </select>

                            <button className="pay-button" onClick={handlePayment}>Confirm Payment</button>
                        </div>
                    )}
                </>
            )}
        </div>
    );
};

export default Payments;
