import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { io } from "socket.io-client";
import { Table, Button, Modal, notification, Switch, Layout, Menu } from "antd";
import {
    HomeOutlined,
    BellOutlined,
    LogoutOutlined,
    UserOutlined,
    BookOutlined,
    CalendarOutlined,
    BulbOutlined
} from "@ant-design/icons";
import axios from "axios";
import "antd/dist/reset.css";

const { Header, Content, Sider } = Layout;
const socket = io("http://localhost:5000");

const Home = ({ user, token }) => {
    const [schedule, setSchedule] = useState([]);
    const [currentUser, setCurrentUser] = useState(user || JSON.parse(localStorage.getItem("user")));
    const [darkMode, setDarkMode] = useState(false);
    const navigate = useNavigate();

    useEffect(() => {
        if (currentUser?.role === "gv") {
            fetchTeachingSchedule(currentUser.id);
        }
        const savedMode = localStorage.getItem("darkMode") === "true";
        setDarkMode(savedMode);
    }, [currentUser]);

    const fetchTeachingSchedule = async (teacherId) => {
        try {
            const response = await axios.get(`http://localhost:5000/teacher/teacher/classes/${teacherId}`);
            setSchedule(response.data);
        } catch (error) {
            notification.error({ message: "Lỗi", description: "Không thể tải lịch dạy!" });
        }
    };

    const handleCancelTeaching = async (classId) => {
        Modal.confirm({
            title: "Xác nhận hủy đăng ký",
            content: "Bạn có chắc muốn hủy đăng ký dạy lớp này không?",
            onOk: async () => {
                try {
                    await axios.delete(`http://localhost:5000/teacher/unregister-class/${currentUser.id}/${classId}`);
                    notification.success({ message: "Thành công", description: "Hủy đăng ký thành công!" });
                    fetchTeachingSchedule(currentUser.id);
                    socket.emit("teachingUnregistered", { teacherId: currentUser.id, classId });
                } catch (error) {
                    notification.error({ message: "Lỗi", description: "Không thể hủy đăng ký dạy!" });
                }
            }
        });
    };

    const toggleDarkMode = (checked) => {
        setDarkMode(checked);
        localStorage.setItem("darkMode", checked);
    };

    const columns = [
        { title: "Tên lớp", dataIndex: "name", key: "name" },
        { title: "Loại lớp", dataIndex: "type_mapped", key: "type_mapped" },
        { title: "Khối", dataIndex: "grade", key: "grade" },
        { title: "Số học sinh", dataIndex: "current_student", key: "current_student", render: (text, record) => `${record.current_student} / ${record.max_student}` },
        { title: "Lịch dạy", dataIndex: "schedule", key: "schedule", render: (text) => text.split(", ").map((s, i) => <div key={i}>{s}</div>) },
        {
            title: "Hành động", key: "action", render: (_, record) => (
                <Button danger onClick={() => handleCancelTeaching(record.id)}>Hủy đăng ký</Button>
            )
        }
    ];

    return (
        <Layout style={{ minHeight: "100vh" }}>
            <Sider collapsible>
                <Menu theme="dark" mode="inline">
                    <Menu.Item key="1" icon={<HomeOutlined />}>Trang chủ</Menu.Item>
                    <Menu.Item key="2" icon={<CalendarOutlined />}>Lịch dạy</Menu.Item>
                    <Menu.Item key="3" icon={<BellOutlined />}>Thông báo</Menu.Item>
                    <Menu.Item key="4" icon={<LogoutOutlined />} onClick={() => navigate("/login")}>Đăng xuất</Menu.Item>
                </Menu>
            </Sider>
            <Layout>
                <Header style={{ display: "flex", justifyContent: "space-between", alignItems: "center" }}>
                    <h2 style={{ color: "white" }}>Quản lý Trung Tâm</h2>
                    <Switch checkedChildren={<BulbOutlined />} unCheckedChildren={<BulbOutlined />} checked={darkMode} onChange={toggleDarkMode} />
                </Header>
                <Content style={{ padding: "20px" }}>
                    <h3>Lịch dạy</h3>
                    <Table columns={columns} dataSource={schedule} rowKey="id" />
                </Content>
            </Layout>
        </Layout>
    );
};

export default Home;
