import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "./Home.css";

const Home = ({ user }) => {
    const navigate = useNavigate();
    const [currentUser, setCurrentUser] = useState(user);

    useEffect(() => {
        if (!user) {
            const storedUser = JSON.parse(localStorage.getItem("user"));
            if (!storedUser) {
                navigate("/login");
            } else {
                setCurrentUser(storedUser);
            }
        }
    }, [user, navigate]);

    const handleLogout = () => {
        localStorage.removeItem("user");
        navigate("/login");
    };

    if (!currentUser) {
        return <p>Đang tải...</p>;
    }

    return (
        <div className="home-container">
            {/* Navbar */}
            <nav className="navbar">
                <h1>Trung Tâm Gia Sư</h1>
                <div className="navbar-buttons">
                    {window.location.pathname !== "/home" && <span className="nav-item" onClick={() => navigate("/home")}>🏠 Trang chủ</span>}
                    <span className="nav-item" onClick={() => navigate("/notifications")}>🔔 Thông báo</span>
                    <span className="nav-item" onClick={() => navigate("/settings")}>⚙️ Cài đặt</span>
                    <span className="nav-item" onClick={() => navigate("/profile")}>👤 Thông tin cá nhân</span>
                    <span className="nav-item" onClick={handleLogout}>🚪 Đăng xuất</span>
                </div>
            </nav>

            <div className="main-content">
                {/* Sidebar */}
                <aside className="sidebar">
                    <div className="sidebar-container">
                        <ul>
                            <li className="sidebar-item" onClick={() => navigate("/home")}>🏠 Trang chủ</li>
                            {currentUser.role === "hv" && (
                                <>
                                    <li className="sidebar-item" onClick={() => navigate("/schedule")}>📖 Lịch học</li>
                                    <li className="sidebar-item" onClick={() => navigate("/grades")}>📊 Điểm số</li>
                                    <li className="sidebar-item" onClick={() => navigate("/fees")}>💰 Học phí</li>
                                    <li className="sidebar-item" onClick={() => navigate("/makeup-class")}>🔄 Học bù</li>
                                </>
                            )}
                            {currentUser.role === "gv" && (
                                <>
                                    <li className="sidebar-item" onClick={() => navigate("/teaching-schedule")}>📚 Lịch giảng dạy</li>
                                    <li className="sidebar-item" onClick={() => navigate("/grade-students")}>📝 Chấm điểm học sinh</li>
                                    <li className="sidebar-item" onClick={() => navigate("/attendance")}>⏳ Chấm công</li>
                                </>
                            )}
                            {currentUser.role === "cm" && (
                                <>
                                    <li className="sidebar-item" onClick={() => navigate("/manage-groups")}>📖 Quản lý nhóm học</li>
                                    <li className="sidebar-item" onClick={() => navigate("/finance-reports")}>📊 Báo cáo tài chính</li>
                                    <li className="sidebar-item" onClick={() => navigate("/timetable")}>🗓️ Thời khóa biểu chung</li>
                                    <li className="sidebar-item" onClick={() => navigate("/reports")}>📋 Xem báo cáo học sinh & giáo viên</li>
                                </>
                            )}
                            {currentUser.role === "ad" && (
                                <>
                                    <li className="sidebar-item" onClick={() => navigate("/system-management")}>⚙️ Quản lý hệ thống</li>
                                    <li className="sidebar-item" onClick={() => navigate("/revenue-salary")}>📊 Doanh thu & lương</li>
                                    <li className="sidebar-item" onClick={() => navigate("/attendance-reports")}>📑 Báo cáo chuyên cần</li>
                                </>
                            )}
                        </ul>
                    </div>
                </aside>

                {/* Nội dung Dashboard */}
                <main className="dashboard">
                    <section className="dashboard-content">
                        <h2>Bảng điều khiển</h2>
                        {currentUser.role === "hv" && <p>📌 Bạn có thể xem lịch học, điểm số và học phí tại đây.</p>}
                        {currentUser.role === "gv" && <p>📌 Bạn có thể xem lịch giảng dạy, nhập điểm và chấm công.</p>}
                        {currentUser.role === "cm" && <p>📌 Quản lý nhóm học, xem báo cáo tài chính và điều chỉnh thời khóa biểu.</p>}
                        {currentUser.role === "ad" && <p>📌 Quản lý hệ thống, lập báo cáo doanh thu & lương, theo dõi điểm danh.</p>}
                    </section>
                </main>
            </div>
        </div>
    );
};

export default Home;
