import React, { useEffect, useState } from "react";
import axios from "axios";
import { Button } from "@/components/ui/button";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";

const Notifications = () => {
    const [notifications, setNotifications] = useState([]);
    const [userRole, setUserRole] = useState("");
    const [newNotification, setNewNotification] = useState({ title: "", message: "", recipient: "users" });
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState("");
    const [isOpen, setIsOpen] = useState(false); // Tr·∫°ng th√°i m·ªü modal
    const token = localStorage.getItem("token");

    useEffect(() => {
        fetchUserRole();
    }, []);

    const fetchUserRole = () => {
        const user = JSON.parse(localStorage.getItem("user"));
        if (user?.role) {
            setUserRole(user.role);
            fetchNotifications(user.role);
        } else {
            setError("Kh√¥ng t√¨m th·∫•y vai tr√≤ ng∆∞·ªùi d√πng.");
        }
    };

    const fetchNotifications = async (userRole) => {
        try {
            const response = await axios.get(`http://localhost:5000/notifications/notifications?role=${userRole}`);
            setNotifications(response.data);
        } catch (error) {
            console.error("L·ªói khi t·∫£i th√¥ng b√°o:", error.response?.data || error.message);
            setError("Kh√¥ng th·ªÉ t·∫£i th√¥ng b√°o.");
        } finally {
            setLoading(false);
        }
    };

    const handleDelete = async (id) => {
        if (!window.confirm("B·∫°n c√≥ ch·∫Øc mu·ªën x√≥a th√¥ng b√°o n√†y?")) return;

        try {
            await axios.delete(`http://localhost:5000/notifications/notifications/${id}`, {
                headers: { Authorization: `Bearer ${token}` },
            });
            setNotifications(notifications.filter(notification => notification.id !== id));
        } catch (error) {
            setError("L·ªói khi x√≥a th√¥ng b√°o.");
        }
    };

    const handleCreate = async () => {
        if (!newNotification.title.trim() || !newNotification.message.trim()) {
            alert("Vui l√≤ng nh·∫≠p ti√™u ƒë·ªÅ v√† n·ªôi dung.");
            return;
        }

        try {
            await axios.post(
                "http://localhost:5000/notifications/notifications",
                newNotification,
                { headers: { Authorization: `Bearer ${token}` } }
            );
            setNewNotification({ title: "", message: "", recipient: "users" });
            fetchNotifications(userRole);
        } catch (error) {
            setError("L·ªói t·∫°o th√¥ng b√°o.");
        }
    };

    const filteredNotifications = notifications.filter(
        (notification) => notification.recipient === "users" || notification.recipient === userRole
    );

    return (
        <Dialog open={isOpen} onOpenChange={setIsOpen}>
            <DialogTrigger asChild>
                <Button variant="outline">üì¢ Th√¥ng b√°o</Button>
            </DialogTrigger>
            <DialogContent className="max-w-md">
                <DialogHeader>
                    <DialogTitle>Th√¥ng b√°o</DialogTitle>
                </DialogHeader>

                {loading ? (
                    <p>‚è≥ ƒêang t·∫£i th√¥ng b√°o...</p>
                ) : error ? (
                    <p className="text-red-500">{error}</p>
                ) : notifications.length > 0 ? (
                    <ul className="space-y-3">
                        {filteredNotifications.map((notification) => (
                            <li key={notification.id} className="border-b pb-2 flex justify-between items-center">
                                <div>
                                    <strong>{notification.title}</strong>: {notification.message}
                                </div>
                                {(userRole === "ad" || userRole === "cm") && (
                                    <Button
                                        onClick={() => handleDelete(notification.id)}
                                        variant="destructive"
                                        size="sm"
                                    >
                                        X√≥a
                                    </Button>
                                )}
                            </li>
                        ))}
                    </ul>
                ) : (
                    <p>Kh√¥ng c√≥ th√¥ng b√°o n√†o.</p>
                )}

                {(userRole === "ad" || userRole === "cm") && (
                    <div className="space-y-2">
                        <h3 className="text-lg font-semibold">‚ûï Th√™m th√¥ng b√°o</h3>
                        <input
                            type="text"
                            placeholder="Ti√™u ƒë·ªÅ"
                            value={newNotification.title}
                            onChange={(e) => setNewNotification({ ...newNotification, title: e.target.value })}
                            className="w-full p-2 border rounded"
                        />
                        <textarea
                            placeholder="N·ªôi dung"
                            value={newNotification.message}
                            onChange={(e) => setNewNotification({ ...newNotification, message: e.target.value })}
                            className="w-full p-2 border rounded"
                        />
                        <select
                            value={newNotification.recipient}
                            onChange={(e) => setNewNotification({ ...newNotification, recipient: e.target.value })}
                            className="w-full p-2 border rounded"
                        >
                            <option value="users">T·∫•t c·∫£ ng∆∞·ªùi d√πng</option>
                            <option value="hv">H·ªçc vi√™n</option>
                            <option value="gv">Gi√°o vi√™n</option>
                            <option value="cm">Qu·∫£n l√Ω</option>
                            <option value="ad">Admin</option>
                        </select>
                        <Button onClick={handleCreate} className="w-full bg-green-500">
                            Th√™m
                        </Button>
                    </div>
                )}
            </DialogContent>
        </Dialog>
    );
};

export default Notifications;
