import { useEffect, useState } from "react";

const TeacherClassRegister = () => {
    const [classes, setClasses] = useState([]);
    const [assignedClasses, setAssignedClasses] = useState([]);
    const teacherId = JSON.parse(localStorage.getItem("user"))?.id;

    useEffect(() => {
        // Lấy danh sách lớp chưa có giáo viên
        fetch("http://localhost:5000/teacher/classes/unassigned")
            .then((res) => res.json())
            .then((data) => setClasses(data))
            .catch((error) => console.error("❌ Lỗi khi tải danh sách lớp:", error));

        // Lấy danh sách lớp giáo viên đã đăng ký
        fetch(`http://localhost:5000/teacher/${teacherId}/assigned-classes`)
            .then((res) => res.json())
            .then((data) => setAssignedClasses(data))
            .catch((error) => console.error("❌ Lỗi khi tải lớp đã đăng ký:", error));
    }, [teacherId]);

    const isScheduleConflict = (newSchedule) => {
        for (const cls of assignedClasses) {
            if (cls.schedule && newSchedule && cls.schedule === newSchedule) {
                return true;
            }
        }
        return false;
    };

    const handleRegisterClass = (classId, className, schedule) => {
        if (isScheduleConflict(schedule)) {
            alert(`⚠️ Không thể đăng ký lớp ${className} vì trùng lịch với lớp đã dạy.`);
            return;
        }

        fetch(`http://localhost:5000/teacher/classes/${classId}/assign-teacher`, {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({ teacher_id: teacherId }),
        })
            .then((res) => res.json())
            .then((data) => {
                alert(`✅ Bạn đã đăng ký dạy lớp ${className} thành công!`);
                // Cập nhật danh sách lớp
                setClasses(classes.filter((cls) => cls.id !== classId));
                setAssignedClasses([...assignedClasses, { id: classId, name: className, schedule }]);
            })
            .catch((error) => console.error("❌ Lỗi khi đăng ký lớp:", error));
    };

    return (
        <div>
            <h2>Danh sách lớp chưa có giáo viên</h2>
            <table border="1" cellPadding="8" cellSpacing="0">
                <thead>
                    <tr>
                        <th>Tên lớp</th>
                        <th>Trình độ</th>
                        <th>Khối</th>
                        <th>Sĩ số</th>
                        <th>Lịch học</th>
                        <th>Hành động</th>
                    </tr>
                </thead>
                <tbody>
                    {classes.length > 0 ? (
                        classes.map((cls) => (
                            <tr key={cls.id}>
                                <td>{cls.name}</td>
                                <td>{cls.type_mapped}</td>
                                <td>{cls.grade}</td>
                                <td>{cls.current_student}/{cls.max_student}</td>
                                <td>{cls.schedule || "Chưa có lịch"}</td>
                                <td>
                                    <button onClick={() => handleRegisterClass(cls.id, cls.name, cls.schedule)}>
                                        Đăng ký dạy
                                    </button>
                                </td>
                            </tr>
                        ))
                    ) : (
                        <tr>
                            <td colSpan="6" style={{ textAlign: "center" }}>Không có lớp nào cần giáo viên.</td>
                        </tr>
                    )}
                </tbody>
            </table>
        </div>
    );
};

export default TeacherClassRegister;
