import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import DraggablePopup from "./DraggablePopup";
import "./Profile.css";

const Profile = () => {
    const navigate = useNavigate();
    const [user, setUser] = useState(null);
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [isEditOpen, setIsEditOpen] = useState(false);
    const [popupMessage, setPopupMessage] = useState("");
    const [isPopupSuccess, setIsPopupSuccess] = useState(null);
    const [editUser, setEditUser] = useState({ fullName: "", email: "", phone: "" });
    const [isEditing, setIsEditing] = useState(false);
    const [updatedInfo, setUpdatedInfo] = useState({ fullName: "", email: "", phone: "" });
    const [message, setMessage] = useState(null);
    const [oldPassword, setOldPassword] = useState("");
    const [newPassword, setNewPassword] = useState("");
    const [confirmPassword, setConfirmPassword] = useState("");


    useEffect(() => {
        const storedUser = JSON.parse(localStorage.getItem("user"));
        if (!storedUser) {
            navigate("/login");
        } else {
            setUser(storedUser);
            setEditUser({ fullName: storedUser.fullName, email: storedUser.email, phone: storedUser.phone || "" });
        }
    }, [navigate]);
    const handleSaveChanges = async () => {

        // Regex ki·ªÉm tra ƒë·ªãnh d·∫°ng email chu·∫©n
        const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;

        // Regex ki·ªÉm tra s·ªë ƒëi·ªán tho·∫°i VN (b·∫Øt ƒë·∫ßu b·∫±ng 0, c√≥ 10 s·ªë)
        const phoneRegex = /^0\d{9}$/;

        if (!editUser.fullName || !editUser.email) {
            setPopupMessage("Vui l√≤ng ƒëi·ªÅn ƒë·∫ßy ƒë·ªß h·ªç t√™n v√† email!");
            setIsPopupSuccess(false);
            return;
        }

        if (!emailRegex.test(editUser.email)) {
            setPopupMessage("Email kh√¥ng h·ª£p l·ªá!");
            setIsPopupSuccess(false);
            return;
        }

        if (editUser.phone && !phoneRegex.test(editUser.phone)) {
            setPopupMessage("S·ªë ƒëi·ªán tho·∫°i kh√¥ng h·ª£p l·ªá! (V√≠ d·ª•: 0912345678)");
            setIsPopupSuccess(false);
            return;
        }

        try {
            const response = await fetch("http://localhost:5000/student/update-profile", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({
                    id: user.id,
                    fullName: editUser.fullName,
                    email: editUser.email,
                    phone: editUser.phone || "",
                }),
            });

            const data = await response.json();
            if (response.ok) {
                setPopupMessage(data.message || "C·∫≠p nh·∫≠t th√†nh c√¥ng!");
                setIsPopupSuccess(true);

                // ‚úÖ C·∫≠p nh·∫≠t `localStorage` v·ªõi th√¥ng tin m·ªõi
                const updatedUser = { ...user, fullName: editUser.fullName, email: editUser.email, phone: editUser.phone };
                localStorage.setItem("user", JSON.stringify(updatedUser));
                setUser(updatedUser); // C·∫≠p nh·∫≠t state React

                setIsEditOpen(false); // ƒê√≥ng modal sau khi l∆∞u th√†nh c√¥ng
            } else {
                setPopupMessage(data.message || "C√≥ l·ªói x·∫£y ra!");
                setIsPopupSuccess(false);
            }
        } catch (error) {
            setPopupMessage("L·ªói k·∫øt n·ªëi ƒë·∫øn server!");
            setIsPopupSuccess(false);
        }
    };


    const closePopup = () => {
        setIsModalOpen(false);
        setPopupMessage("");
        setIsPopupSuccess(null);
    };

    if (!user) {
        return <p>ƒêang t·∫£i...</p>;
    }
    const handlePasswordChanges = async () => {
        if (!oldPassword || !newPassword || !confirmPassword) {
            setPopupMessage("Vui l√≤ng nh·∫≠p ƒë·∫ßy ƒë·ªß th√¥ng tin!");
            setIsPopupSuccess(false);
            return;
        }

        if (newPassword !== confirmPassword) {
            setPopupMessage("M·∫≠t kh·∫©u m·ªõi kh√¥ng kh·ªõp!");
            setIsPopupSuccess(false);
            return;
        }

        try {
            const response = await fetch("http://localhost:5000/student/update-password", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({
                    id: user.id,
                    oldPassword,  // G·ª≠i m·∫≠t kh·∫©u c≈© l√™n server
                    newPassword,
                }),
            });

            const data = await response.json();
            if (response.ok) {
                setPopupMessage("ƒê·ªïi m·∫≠t kh·∫©u th√†nh c√¥ng!");
                setIsPopupSuccess(true);

                // ƒê·∫∑t l·∫°i c√°c √¥ nh·∫≠p m·∫≠t kh·∫©u
                setOldPassword("");
                setNewPassword("");
                setConfirmPassword("");

                // ƒê√≥ng popup sau 2 gi√¢y
                setTimeout(() => setIsModalOpen(false), 2000);
            } else {
                setPopupMessage(data.message || "M·∫≠t kh·∫©u c≈© kh√¥ng ƒë√∫ng!");
                setIsPopupSuccess(false);
            }
        } catch (error) {
            setPopupMessage("L·ªói k·∫øt n·ªëi ƒë·∫øn server!");
            setIsPopupSuccess(false);
        }
    };



    return (
        <div className="profile-container">
            <h2>üë§ Th√¥ng tin c√° nh√¢n</h2>
            <div className="profile-card">
                <p><strong>üë§ H·ªç v√† t√™n:</strong> {user.fullName}</p>
                <p><strong>üìß Email:</strong> {user.email}</p>
                <p><strong>üìû S·ªë ƒëi·ªán tho·∫°i:</strong> {user.phone || "Ch∆∞a c·∫≠p nh·∫≠t"}</p>
                <p><strong>üé≠ Vai tr√≤:</strong> {user.role.toUpperCase()}</p>
            </div>

            <button onClick={() => setIsEditOpen(true)} className="edit-btn">üìù Ch·ªânh s·ª≠a</button>
            <button onClick={() => setIsModalOpen(true)} className="change-password-btn">üîë ƒê·ªïi m·∫≠t kh·∫©u</button>
            <button onClick={() => navigate("/home")} className="back-btn">‚¨ÖÔ∏è Quay l·∫°i</button>
            {/* ƒê·ªïi m·∫≠t kh·∫©u s·∫Ω ƒë∆∞·ª£c hi·ªÉn th·ªã trong DraggablePopup */}
            {isModalOpen && (
                <DraggablePopup>
                    <div className="modal-header popup-header">
                        <h4>ƒê·ªïi m·∫≠t kh·∫©u</h4>
                    </div>
                    <div className="modal-body">
                        {popupMessage && (
                            <p style={{ color: isPopupSuccess ? "green" : "red" }}>
                                {popupMessage}
                            </p>
                        )}
                        <input
                            type="password"
                            placeholder="M·∫≠t kh·∫©u c≈©"
                            value={oldPassword}
                            onChange={(e) => setOldPassword(e.target.value)}
                        />
                        <input
                            type="password"
                            placeholder="M·∫≠t kh·∫©u m·ªõi"
                            value={newPassword}
                            onChange={(e) => setNewPassword(e.target.value)}
                        />
                        <input
                            type="password"
