// components/MakeUpClasses.js
import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

const MakeUpClasses = () => {
    const [makeUpClasses, setMakeUpClasses] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        // Kiểm tra xem người dùng đã đăng nhập chưa
        const storedUser = JSON.parse(localStorage.getItem("user"));
        if (!storedUser) {
            navigate("/login");
            return;
        }

        // Gọi API để lấy lớp học bù
        fetchMakeUpClasses(storedUser.studentId);
    }, [navigate]);

    const fetchMakeUpClasses = async (studentId) => {
        try {
            const response = await fetch(`http://localhost:5000/student/make-up-classes/${studentId}`);
            if (!response.ok) throw new Error("Lỗi khi tải lớp học bù");
            const data = await response.json();
            setMakeUpClasses(data);
        } catch (error) {
            console.error("Lỗi:", error);
            setMakeUpClasses([]);
        }
    };

    return (
        <div>
            <h2>Danh sách lớp học bù</h2>
            {makeUpClasses.length === 0 ? (
                <p>Chưa có lớp học bù nào.</p>
            ) : (
                <ul>
                    {makeUpClasses.map((makeUpClass, index) => (
                        <li key={index}>
                            <p><strong>Lớp học:</strong> {makeUpClass.class_name}</p>
                            <p><strong>Ngày học bù:</strong> {new Date(makeUpClass.make_up_date).toLocaleDateString()}</p>
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
};

export default MakeUpClasses;
