import React, { useState, useEffect } from "react";
import axios from "axios";
import "./TeacherGradesInput.css"; // Import CSS

const TeacherGradeInput = () => {
    const [classes, setClasses] = useState([]); // Ch·ªâ l∆∞u danh s√°ch t√™n nh√≥m
    const [selectedClass, setSelectedClass] = useState(""); // Nh√≥m ƒë√£ ch·ªçn
    const [students, setStudents] = useState([]); // H·ªçc vi√™n trong nh√≥m
    const [scores, setScores] = useState({}); // L∆∞u ƒëi·ªÉm nh·∫≠p v√†o

    const teacherId = 1; // üî• Gi·∫£ ƒë·ªãnh ID gi√°o vi√™n, b·∫°n c√≥ th·ªÉ l·∫•y t·ª´ context ho·∫∑c localStorage

    // L·∫•y danh s√°ch l·ªõp c·ªßa gi√°o vi√™n hi·ªán t·∫°i
    useEffect(() => {
        axios.get(`http://localhost:5000/teacher/classes/${teacherId}`)
            .then(res => {
                const classNames = res.data.map(cls => ({ id: cls.id, name: cls.name }));
                setClasses(classNames);
            })
            .catch(err => console.error("‚ùå L·ªói khi l·∫•y danh s√°ch l·ªõp:", err));
    }, []);

    // L·∫•y danh s√°ch h·ªçc vi√™n khi ch·ªçn nh√≥m
    useEffect(() => {
        if (!selectedClass) return;
        axios.get(`http://localhost:5000/teacher/students?class_id=${selectedClass}`)
            .then(res => setStudents(res.data))
            .catch(err => console.error("‚ùå L·ªói khi l·∫•y danh s√°ch h·ªçc vi√™n:", err));
    }, [selectedClass]);

    // Ki·ªÉm tra ƒëi·ªÉm h·ª£p l·ªá (1-9)
    const handleScoreChange = (studentId, value) => {
        if (/^[1-9]$/.test(value) || value === "") {
            setScores(prev => ({ ...prev, [studentId]: value }));
        }
    };

    const handleSubmit = (studentId) => {
        const score = parseInt(scores[studentId], 10);

        if (isNaN(score) || score < 1 || score > 9) {
            return alert("‚ö†Ô∏è Vui l√≤ng nh·∫≠p ƒëi·ªÉm t·ª´ 1 ƒë·∫øn 9!");
        }

        if (!selectedClass) {
            return alert("L·ªói: Kh√¥ng t√¨m th·∫•y class_id!");
        }

        axios.post("http://localhost:5000/teacher/grades", {
            student_id: studentId,
            class_id: selectedClass,
            score,
            exam_name: "B√†i ki·ªÉm tra",
            exam_date: new Date().toISOString().split('T')[0]
        }, {
            headers: {
                "Content-Type": "application/json"
            }
        })
            .then(() => {
                alert("‚úÖ Nh·∫≠p ƒëi·ªÉm th√†nh c√¥ng!");
                setScores(prev => ({ ...prev, [studentId]: "" }));
            })
            .catch(err => {
                console.error("‚ùå L·ªói khi nh·∫≠p ƒëi·ªÉm:", err);
                alert("‚ö†Ô∏è L·ªói khi nh·∫≠p ƒëi·ªÉm! Vui l√≤ng th·ª≠ l·∫°i.");
            });
    };

    return (
        <div className="teacher-container">
            <h2 className="teacher-title">Nh·∫≠p ƒêi·ªÉm H·ªçc Vi√™n</h2>

            {/* Dropdown ch·ªçn nh√≥m (ch·ªâ hi·ªÉn th·ªã t√™n nh√≥m) */}
            <div className="teacher-select-group">
                <label>Ch·ªçn Nh√≥m: </label>
                <select value={selectedClass} onChange={(e) => setSelectedClass(e.target.value)}>
                    <option value="">-- Ch·ªçn nh√≥m --</option>
                    {classes.map(cls => (
                        <option key={cls.id} value={cls.id}>
                            {cls.name}
                        </option>
                    ))}
                </select>
            </div>

            <table className="teacher-table">
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>H·ªç T√™n</th>
                        <th>Nh·∫≠p ƒêi·ªÉm</th>
                        <th>X√°c Nh·∫≠n</th>
                    </tr>
                </thead>
                <tbody>
                    {students.map(student => (
                        <tr key={student.id}>
                            <td>{student.id}</td>
                            <td>{student.fullName || "(Kh√¥ng c√≥ t√™n)"}</td>
                            <td>
                                <input
                                    type="text"
                                    className="teacher-input"
                                    value={scores[student.id] || ""}
                                    onChange={(e) => handleScoreChange(student.id, e.target.value)}
                                    placeholder="1-9"
                                    maxLength={2}
                                />
                            </td>
                            <td>
                                <button
                                    onClick={() => handleSubmit(student.id)}
                                    className="teacher-btn"
                                >
                                    X√°c nh·∫≠n
                                </button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default TeacherGradeInput;
