import React, { useEffect, useState } from 'react';
import axios from 'axios';
import jsPDF from 'jspdf';
import 'jspdf-autotable';
import './Payments.css';

const Payments = ({ studentId }) => {
    const [fees, setFees] = useState([]);
    const [message, setMessage] = useState('');
    const [showInvoice, setShowInvoice] = useState(false);

    useEffect(() => {
        if (studentId) {
            fetchFees();
        } else {
            setMessage('Không tìm thấy ID học viên!');
        }
    }, [studentId]);

    const fetchFees = async () => {
        setMessage('');
        try {
            const response = await axios.get(`http://localhost:5000/student/fees/${studentId}`);
            const newFees = Array.isArray(response.data) ? response.data : [];
            setFees(newFees);
        } catch (error) {
            console.error('Lỗi khi tải học phí:', error);
            setMessage('Có lỗi xảy ra khi tải dữ liệu!');
            setFees([]);
        }
    };

    const handlePayment = async () => {
        if (window.confirm('Bạn có chắc chắn muốn thanh toán học phí?')) {
            try {
                const response = await axios.post(`http://localhost:5000/student/pay`, { studentId });
                if (response.data.success) {
                    setMessage('Thanh toán thành công!');
                    fetchFees();
                    setShowInvoice(true);
                } else {
                    setMessage('Thanh toán thất bại!');
                }
            } catch (error) {
                console.error('Lỗi thanh toán:', error);
                setMessage('Có lỗi xảy ra khi thanh toán!');
            }
        }
    };

    const generateInvoice = () => {
        const doc = new jsPDF();
        doc.text('Hóa đơn thanh toán học phí', 10, 10);
        doc.autoTable({
            head: [['Tên lớp', 'ID lớp', 'Số tiền', 'Trạng thái']],
            body: fees.map(fee => [
                fee.class_name || 'Không có tên',
                fee.class_id,
                `${fee.amount.toLocaleString('vi-VN')} VNĐ`,
                fee.is_paid ? 'Đã thanh toán' : 'Chưa thanh toán'
            ]),
        });
        doc.save(`hoa_don_${studentId}.pdf`);
    };

    const hasUnpaid = fees.some(fee => !fee.is_paid);

    return (
        <div className="fee-container">
            <h2>Thanh toán học phí</h2>
            <table className="fee-table">
                <thead>
                    <tr>
                        <th>Tên lớp</th>
                        <th>ID lớp</th>
                        <th>Số tiền</th>
                        <th>Trạng thái</th>
                    </tr>
                </thead>
                <tbody>
                    {fees.length > 0 ? (
                        fees.map(fee => (
                            <tr key={fee.class_id}>
                                <td>{fee.class_name || 'Không có tên'}</td>
                                <td>{fee.class_id}</td>
                                <td>{fee.amount.toLocaleString('vi-VN')} VNĐ</td>
                                <td className={fee.is_paid ? 'paid' : 'unpaid'}>
                                    {fee.is_paid ? 'Đã thanh toán' : 'Chưa thanh toán'}
                                </td>
                            </tr>
                        ))
                    ) : (
                        <tr>
                            <td colSpan="4">Không có dữ liệu học phí</td>
                        </tr>
                    )}
                </tbody>
            </table>
            <button
                className={`pay-button ${!hasUnpaid ? 'disabled' : ''}`}
                onClick={handlePayment}
                disabled={!hasUnpaid}
            >
                Xác nhận thanh toán
            </button>
            {showInvoice && (
                <button className="invoice-button" onClick={generateInvoice}>
                    Xuất hóa đơn PDF
                </button>
            )}
            {message && <div className="message">{message}</div>}
        </div>
    );
};

export default Payments;
